SELECT A.LINK_YR ||'-'|| A.DATA_DMND_SQNO AS DATA_SQNO /* 자료순번 */,
    /*CSPIIT003QExcelExportSelect*/ F_CODE_NM('1000798',A.MTRL_RQS_DVSN_CD) AS F_CODE_NM,
    /*자료요청구분코드*/ TO_CHAR(TO_DATE(A.DMND_YMD),'YYYY-MM-DD') AS DMND_YMD /* 요청일자 */,
    /*요청일*/ D.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*요청자부서명*/ B.USER_NM AS USER_NM /* 사용자명 */,
    /*요청자명*/ A.RQS_TIT_NM,
    /*요청제목*/ C.USER_NM AS USER_NM /* 사용자명 */,
    /*처리담당자명*/ TO_CHAR(TO_DATE(A.GPCD_PRCS_YMD),'YYYY-MM-DD') AS GPCD_PRCS_YMD /* 처리일자 */,
    /*처리일자*/ F.ADT_MTTR_NM AS ADT_MTTR_NM /* 감사사항명 */,
    ( SELECT COUNT(*)
FROM TBCSPIIT028D S1 WHERE S1.LINK_YR = A.LINK_YR AND S1.DATA_DMND_SQNO = A.DATA_DMND_SQNO ) AS SUB_CNT, ( SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD='1000856' AND CD = A.CHF_APV_STA_CD ) AS APV_STA_CD_NM, /*시스템운영과장 승인반려 상태코드*/ G.USER_NM AS DRTR_NAM FROM TBCSPIIT028M A, /*PC장비관리테이블*/ TBDCMACM001M B, /*사용자테이블(요청자)*/ TBDCMACM001M C, /*사용자테이블(처리자)*/ TBDCMACM002M D, /*부서코드(요청자)*/ TBDCMACM002M E, /*부서코드(처리자)*/ TB_BADDED001M F, TBDCMACM001M G /*사용자테이블(책임자)*/ WHERE A.RQSTR_ENO = B.TRGT_NOP_ENO(+) AND A.PRCS_PIC_ENO = C.TRGT_NOP_ENO(+) AND A.DMND_DEPT_CD = D.SRECS_DEPT_CD(+) AND C.SRECS_DEPT_CD = E.SRECS_DEPT_CD(+) AND A.CVLCPT_ADT_YR = F.ADT_YR(+) AND A.ADT_NO = F.ADT_NO(+) AND A.DRTR_CNB = G.TRGT_NOP_ENO(+) AND A.DMND_YMD &gt;= #strRQS_DTS# AND A.DMND_YMD &lt;= #strRQS_DTE# AND A.MTRL_RQS_DVSN_CD = #strMTRL_RQS_DVSN_CD# AND (D.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR E.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.RQS_TIT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%' OR A.PRCS_CN LIKE '%'||#strSEARCH_NM#||'%') AND B.USER_NM LIKE '%'||#strRQR_NAM#||'%' AND C.USER_NM LIKE '%'||#strHNDL_PEN_NAM#||'%' AND ( A.RLS_YN = 'Y' OR A.RQSTR_ENO = #RQSTR_ENO#) AND A.PRCS_YMD IS NULL AND A.PRCS_YMD IS NOT NULL ORDER BY A.LINK_YR DESC, TO_NUMBER(A.DATA_DMND_SQNO) DESC