SELECT A.LINK_YR ||'-'|| A.DATA_DMND_SQNO AS DATA_SQNO /* 자료순번 */,
    A.LINK_YR /* 연계년도 */,
    A.DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.MTRL_RQS_DVSN_CD,
    /*자료요청구분코드*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.RQSTR_ENO = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RLS_YN, /*공개여부*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ A.PRCS_YMD, /*처리일*/ A.PRCS_PIC_ENO, /*처리자전산번호*/ (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.PRCS_PIC_ENO = Z.TRGT_NOP_ENO ) AS HNDL_PEN_NAM, /*처리자명*/ A.PRCS_CN, /*처리내용*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT028L Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.LINK_YR = A.LINK_YR AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '1') AS DMND_DOC_ID, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT028L Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.LINK_YR = A.LINK_YR AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '2') AS CRU_PRCS_DOC_ID, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT028L Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.LINK_YR = A.LINK_YR AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '3') AS MEDIA_DOC_ID, F_DPT_INFO(A.DMND_DEPT_CD,'1') AS RQS_DPT_NM, (SELECT CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.RQSTR_ENO ) AS CRU_TELNO, A.HNDL_ORD_TXT, /*처리지시*/ A.NTFCTN_RCPT_YMD, /*접수일자*/ A.RQS_HNDL_DT, /*요청처리기한*/ A.CVLCPT_ADT_YR, A.ADT_NO, CASE WHEN B.ADT_YR >= '2016' THEN B.ADT_KDCD ELSE '' END AS AUD_YR_NO_KND_CD, B.ADT_MTTR_NM, B.ADT_KDCD, B.ADT_GRNT_NO, C.INST_CD, C.PROF_RBPRSN_INST_NM, A.ATRZ_DOC_NO, D.ATRZ_STTS_SECD, A.CHF_APV_STA_CD, A.DRTR_CNB, /*책임자전산번호*/ (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M ZZ WHERE A.DRTR_CNB = ZZ.TRGT_NOP_ENO ) AS DRTR_NAM, /*책임자명*/ NVL(A.SEN_YN,'N') AS SEN_YN, A.PRCS_PIC_ENO AS OLD_HNDL_PEN_CNB, A.RSLT_YN, A.RSLT_RMK_TXT, (SELECT USR_DFN_TXT_1
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD='1000882' AND CD='7') AS CHAR_CNB, /*기본책임자전산번호*/ (SELECT USR_DFN_TXT_2
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD='1000882' AND CD='7') AS CHAR_NAM /*기본책임자명*/ FROM TBCSPIIT028M A, TB_BADDED001M B ,TBDCMACM015M C, TBDCMACM022L D WHERE 1=1 AND A.LINK_YR ||'-'|| A.DATA_DMND_SQNO = #DATA_DMND_SQNO# AND A.LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND A.DATA_DMND_SQNO = #DATA_DMND_SQNO# AND A.ATRZ_DOC_NO = #ATRZ_DOC_NO# AND A.CVLCPT_ADT_YR = B.ADT_YR(+) AND A.ADT_NO = B.ADT_NO(+) AND A.INST_CD = C.INST_CD(+) AND A.INST_CD = C.INST_CD(+) AND A.ATRZ_DOC_NO = D.ATRZ_DOC_NO(+) /*AND A.COPUT_BRD_DVSN_CD = '05' 게시판구분코드 - 전산개선요청*/