SELECT A.DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.MTRL_RQS_DVSN_CD,
    /*자료요청구분코드*/ TO_CHAR(TO_DATE(A.DMND_YMD),'YYYY-MM-DD') AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ (SELECT S1.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M S1 WHERE S1.SRECS_DEPT_CD = A.DMND_DEPT_CD ) ||' '|| (SELECT Z.USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.RQSTR_ENO = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RLS_YN, /*공개여부*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ TO_CHAR(TO_DATE(A.PRCS_YMD),'YYYY-MM-DD') AS PRCS_YMD, /*처리일*/ A.PRCS_PIC_ENO, /*처리자전산번호*/ (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.PRCS_PIC_ENO = Z.TRGT_NOP_ENO ) AS HNDL_PEN_NAM, /*처리자명*/ A.PRCS_CN, /*처리내용*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT009M Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '1' AND Z.COPUT_BRD_DVSN_CD = '01') AS DMND_DOC_ID, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT009M Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.COPUT_BRD_DVSN_CD = '01' AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '2') AS CRU_PRCS_DOC_ID, PRPS_YE, PRPS_SLN, (SELECT S1.CD_NM
FROM TBDCMACM013C S1 WHERE S1.CD = DGE1_MNU_DVSN_CD AND S1.CMM_CD_DVSN_CD = '1000794' ) AS DGE1_MNU_DVSN_CD /*1차메뉴구분코드*/, (SELECT CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.RQSTR_ENO ) AS CRU_TELNO, NVL(SEN_YN,'N') AS SEN_YN, (SELECT S1.CD_NM
FROM TBDCMACM013C S1 WHERE S1.CD = COPUT_BRD_DVSN_CD AND S1.CMM_CD_DVSN_CD = '1000793' ) AS COPUT_BRD_DVSN_NM, A.PRCS_PIC_ENO AS OLD_HNDL_PEN_CNB, WORK_YE, WORK_SNO, (SELECT S1.CD_NM
FROM TBDCMACM013C S1 WHERE S1.CD = TASK_SECD AND S1.CMM_CD_DVSN_CD = '1000809' ) AS TASK_SECD, MNU_CD_1, MNU_CD_2, TO_CHAR(TO_DATE(A.DIST_DT),'YYYY-MM-DD') AS DIST_DT, DIST_CHGR_CNB, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.DIST_CHGR_CNB = Z.TRGT_NOP_ENO ) AS DIST_CHGR_NAM ,CASE WHEN WORK_YE IS NOT NULL THEN WORK_YE||'-'||WORK_SNO ELSE '' END WORK_SRNO ,WORK_TXT ,HNDL_ORD_TXT FROM TBCSPIIT002M A WHERE A.DATA_DMND_SQNO = #DATA_DMND_SQNO# AND A.COPUT_BRD_DVSN_CD = #strCOPUT_BRD_DVSN_CD# /*게시판구분코드 - 전산개선요청*/