SELECT (SUBSTR(A.MEDIA_MNG_CD,0,4) || '_' || SUBSTR(A.MEDIA_MNG_CD,6)) AS SUBSTR,
    /*PC관리코드*/ F_CODE_NM('1000844',A.MEDIA_KND_CD) AS F_CODE_NM,
    /*매체종류코드*/ F_CODE_NM('1000845',A.MEDIA_STAT_CD) AS F_CODE_NM,
    /*매체상태코드*/ (CASE WHEN (SELECT S1.ACTL_OGDP_DEPT_CD AS ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO)= '880880' THEN '퇴직자' ELSE F_CODE_NM('1000144',(SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO)) END)AS CNSTN_MBCMT_DEPT_NM, F_USR_INFO(A.TRGT_NOP_ENO,'2') AS USER_NM, /*전산번호*/ F_CODE_NM('1000173' , f_usr_info(A.TRGT_NOP_ENO,'3')) AS JBGD_NM, A.MEDIA_SRNO, /*시리얼번호*/ TO_CHAR(TO_DATE(A.MEDIA_REG_DT),'YYYY-MM-DD') AS MEDIA_REG_DT, /*도입일*/ A.MEDIA_MNG_RMK, /*비고*/ A.TRGT_NOP_ENO, (SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) AS SRECS_DEPT_CD, /*부서코드*/ (SELECT S1.RANK_NM_2
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) AS RANK_NM2, /*호칭*/ CASE WHEN SUBSTR((SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO),0,3)||'000' = (SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) THEN F_DPT_INFO((SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO),'1') ELSE F_DPT_INFO(SUBSTR((SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO),0,3)||'000','1') END AS H_DPT_NM, B.USR_DFN_TXT_3, TO_CHAR(TO_DATE(A.MEDIA_CHK_DT),'YYYY-MM-DD') AS MEDIA_CHK_DT, /*최종점검일*/ A.DTIL_DVSN_CD, /*용량*/ F_CODE_NM('1000960' , A.DTIL_DVSN_CD) AS DTIL_DVSN_NM, /*용량*/ A.WAY_DVSN_CD, /*드라이브방식*/ F_CODE_NM('1000954' , A.WAY_DVSN_CD) AS WAY_DVSN_NM, A.MEDIA_REG_CNT /*차수*/ FROM TBCSPIIT036M A /*보안매체관리테이블*/ ,TBDCMACM013C B WHERE 1=1 AND F_USR_INFO(A.TRGT_NOP_ENO,'5') = B.CD(+) AND B.CMM_CD_DVSN_CD(+) = '1000144' AND A.MEDIA_KND_CD = #strMEDIA_KND_CD# AND A.MEDIA_STAT_CD = #MEDIA_STAT_CD# AND (SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) = #strDPT_CD# AND (A.MEDIA_MNG_RMK LIKE '%'||#strMEDIA_MNG_RMK#||'%' OR A.ETC_MTTR_CN LIKE '%'||#strMEDIA_MNG_RMK#||'%') AND (F_USR_INFO(A.TRGT_NOP_ENO,'2')) LIKE '%'||#strUSER_NM#||'%' ORDER BY B.USR_DFN_TXT_3 ASC