SELECT SUBSTR(A.MEDIA_MNG_CD,0,4) AS LINK_YR /* 연계년도 */,
    SUBSTR(A.MEDIA_MNG_CD,6) AS SUBSTR,
    /*미디어관리코드*/ SUBSTR(A.MEDIA_MNG_CD,0,4) ||'_'|| SUBSTR(A.MEDIA_MNG_CD,6) AS SUBSTR,
    A.MEDIA_SRNO,
    /*시리얼번호*/ A.MEDIA_KND_CD,
    /*미디어종류코드*/ ( SELECT AS CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000144' AND S1.CD <> '000000000' AND S1.CD = (SELECT S1.SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) ) AS CNSTN_MBCMT_DEPT_NM, ( SELECT S1.USER_NM /* 사용자명 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) AS USER_NM, A.TRGT_NOP_ENO, /*전산번호*/ ( SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) AS SRECS_DEPT_CD, /*부서코드*/ A.MEDIA_STAT_CD, /*미디어상태코드*/ A.MEDIA_REG_DT, /*도입일*/ A.MEDIA_MNG_RMK, /*비고*/ A.ETC_MTTR_CN, A.TRGT_NOP_ENO AS OLD_CNB, ( SELECT S1.USER_NM /* 사용자명 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) AS OLD_USR_NAM, ( SELECT CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000173' AND S1.CD <> '000000000' AND S1.CD = (SELECT S2.JBGD_SECD /* 직급구분코드 */
FROM TBDCMACM001M S2 WHERE S2.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) ) AS JBGD_NM, A.MEDIA_SRNO AS OLD_MEDIA_SRNO, /*이전 미디어시리얼번호*/ A.MEDIA_STAT_CD AS OLD_MEDIA_STAT_CD, /*이전 미디어상태코드*/ A.MEDIA_CHK_DT, /*최종점검일*/ A.DTIL_DVSN_CD, /*용량*/ A.WAY_DVSN_CD, /*드라이브방식*/ A.MEDIA_REG_CNT /*차수*/ FROM TBCSPIIT036M A /*보안매체관리테이블*/ WHERE 1=1 AND A.MEDIA_KND_CD = #strMEDIA_KND_CD# AND A.MEDIA_STAT_CD = #strMEDIA_STAT_CD# AND A.MEDIA_SRNO LIKE '%' || #strMEDIA_SRNO# || '%' AND (SELECT S1.ACTL_OGDP_DEPT_CD /* 실제소속부서코드 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO) = #strDPT_CD# AND (A.MEDIA_MNG_RMK LIKE '%'||#strMEDIA_MNG_RMK#||'%' OR A.ETC_MTTR_CN LIKE '%'||#strMEDIA_MNG_RMK#||'%') AND (F_USR_INFO(A.TRGT_NOP_ENO,'2')) LIKE '%'||#strUSER_NM#||'%'