SELECT /*CSPIIT039QExcelExportSelect*/ F_CODE_NM('1000970',A.ACMP_DVSN_CD) AS F_CODE_NM,
    /*자료요청구분코드*/ TO_CHAR(TO_DATE(A.SBMSN_YMD),'YYYY-MM-DD') AS SBMSN_YMD /* 제출일자 */,
    /*요청일*/ C.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*요청자부서명*/ B.USER_NM AS USER_NM /* 사용자명 */,
    /*요청자명*/ A.RQS_TIT_NM,
    /*요청제목*/ F_CODE_NM('1000973',A.PRGRS_STTS_SECD) AS PRGRS_STTS_SECD /* 진행상태구분코드 */,
    F.ADT_MTTR_NM /* 감사사항명 */,
    TO_CHAR(TO_DATE(F.DSPS_RQT_ENFC_YMD),'YYYY-MM-DD') AS DSPS_RQT_ENFC_YMD /* 처분요구시행일자 */,
    F_CODE_NM('1000002', F.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN /* 처분요구종류코드값명 */,
    F.DSPS_RQT_NM /* 처분요구명 */
FROM TBCSPIIT021M A, /*PC장비관리테이블*/ TBDCMACM001M B, /*사용자테이블(요청자)*/ TBDCMACM002M C, /*부서코드(요청자)*/ TBCSPIIT022M F WHERE A.SMT_CNB = B.TRGT_NOP_ENO(+) AND A.DMND_DEPT_CD = C.SRECS_DEPT_CD(+) AND A.ACMP_SNO = F.ACMP_SNO(+) AND COPUT_BRD_DVSN_CD = '02' AND A.SBMSN_YMD &gt;= #strSMT_DTS# AND A.SBMSN_YMD &lt;= #strSMT_DTE# AND (D.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR C.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.RQS_TIT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%') AND B.USER_NM LIKE '%'||#strRQR_NAM#||'%' AND A.SMT_CNB = #strCNB# AND TO_NUMBER(A.CHF_APV_STA_CD) > CASE WHEN ((F_USR_INFO( #strCNB#,'5') = A.DMND_DEPT_CD) ) THEN 0 ELSE 1 END /*요청결재전 표시하지 않음 (요청부서 제외) */ AND A.CHF_APV_STA_CD = #strCHF_APV_STA_CD# AND A.CHF_APV_STA_CD >= #strCHF_APV_STA_CD# AND A.PRGRS_STTS_SECD IS NULL AND A.PRGRS_STTS_SECD IS NOT NULL AND A.DMND_DEPT_CD = #DMND_DEPT_CD# AND A.ACMP_DVSN_CD = #strACMP_DVSN_CD# ORDER BY TO_NUMBER(A.ACMP_SNO) DESC