SELECT DISTINCT A.ACMP_SNO,
    /*입건실적순번*/ A.ACMP_DVSN_CD,
    /*입건실적구분코드*/ A.SBMSN_YMD AS SBMSN_YMD /* 제출일자 */,
    /*제출일자*/ A.RQS_TIT_NM,
    /*요청제목*/ B.USER_NM AS USER_NM /* 사용자명 */,
    /*제출자명*/ A.SMT_CNB,
    /*제출자전산번호*/ A.ATRZ_DOC_NO AS ATRZ_DOC_NO /* 결재문서번호 */,
    /*결재문서번호*/ F.ATRZ_STTS_SECD AS ATRZ_STTS_SECD /* 결재상태구분코드 */,
    /*결재상태코드*/ A.PRGRS_STTS_SECD AS PRGRS_STTS_SECD /* 진행상태구분코드 */,
    /*처리상태코드*/ A.CHF_APV_STA_CD,
    /*정보2과장 승인반려 상태코드*/ (SELECT C.CVLCPT_ADT_YR AS CVLCPT_ADT_YR /* 민원감사년도 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS CVLCPT_ADT_YR, (SELECT C.ADT_NO /* 감사번호 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS ADT_NO, (SELECT C.DSPS_RQT_ENFC_YMD /* 처분요구시행일자 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS DSPS_RQT_ENFC_YMD, (SELECT C.DSPS_RQT_KDCD /* 처분요구종류코드 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS DSPS_RQT_KDCD, (SELECT C.DSPS_RQT_KND_CVN /* 처분요구종류코드값명 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS DSPS_RQT_KND_CVN, (SELECT C.ADT_MTTR_NM /* 감사사항명 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS ADT_MTTR_NM, (SELECT C.DSPS_RQT_NM /* 처분요구명 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS DSPS_RQT_NM, (SELECT C.DSPS_RQT_SQNO /* 처분요구순번 */
FROM TBCSPIIT022M C WHERE A.ACMP_SNO = C.ACMP_SNO) AS DSPS_RQT_SQNO, A.DMND_DEPT_CD, D.CNSTN_MBCMT_DEPT_NM AS RQS_DPT_NM, A.NTFCTN_RCPT_YMD, A.CVLCPT_DOC_ID FROM TBCSPIIT021M A, /*입건실적등록테이블*/ TBDCMACM001M B, /*사용자테이블(제출자)*/ TBDCMACM002M D, /*부서코드(요청자)*/ TBDCMACM022L F /*결재문서내역*/ WHERE A.SMT_CNB = B.TRGT_NOP_ENO(+) AND A.COPUT_BRD_DVSN_CD = '02' /*게시판구분코드 - BARON요청*/ AND A.ATRZ_DOC_NO = F.ATRZ_DOC_NO(+) AND A.DMND_DEPT_CD = D.SRECS_DEPT_CD(+) AND A.SBMSN_YMD &gt;= #strSMT_DTS# AND A.SBMSN_YMD &lt;= #strSMT_DTE# AND (D.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.RQS_TIT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%') AND B.USER_NM LIKE '%'||#strRQR_NAM#||'%' AND PRGRS_STTS_SECD = #PRGRS_STTS_SECD# AND PRGRS_STTS_SECD >= #PRGRS_STTS_SECD# AND A.DMND_DEPT_CD = #DMND_DEPT_CD# AND A.ACMP_DVSN_CD = #strACMP_DVSN_CD#