SELECT A.ACMP_SNO,
    /*자료요청순번*/ A.ACMP_DVSN_CD,
    /*자료요청구분코드*/ A.SBMSN_YMD AS SBMSN_YMD /* 제출일자 */,
    /*요청일*/ A.SMT_CNB,
    /*요청자전산번호*/ (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.SMT_CNB = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*BARON활용내용*/ A.DMND_DEPT_CD, A.ATRZ_DOC_NO ,B.ATRZ_STTS_SECD ,A.CHF_APV_STA_CD ,A.DATA_DMND_SQNO ,A.PRGRS_STTS_SECD ,A.NTFCTN_RCPT_YMD ,A.CVLCPT_DOC_ID FROM TBCSPIIT021M A ,TBDCMACM022L B WHERE 1=1 AND A.ACMP_SNO = #strACMP_SNO# AND A.ATRZ_DOC_NO = #ATRZ_DOC_NO# AND A.ATRZ_DOC_NO = B.ATRZ_DOC_NO(+) AND A.COPUT_BRD_DVSN_CD = '02' /*게시판구분코드 - 전산개선요청*/