SELECT A.SW_MNG_CD,
    A.SW_SERIALNO,
    A.SW_MAKER,
    A.SW_VERSION,
    ( SELECT AS CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000144' AND S1.CD <> '000000000' AND S1.CD = A.SRECS_DEPT_CD ) AS CNSTN_MBCMT_DEPT_NM, A.GET_VERSION, A.USE_VERSION, A.LI_DVSN_TXT, A.LI_GET_CNT, A.LI_USE_CNT, A.LI_STR_DT, A.LI_END_DT, TO_CHAR(TO_DATE(A.LI_STR_DT),'YYYY-MM-DD')||'~'||TO_CHAR(TO_DATE(A.LI_END_DT),'YYYY-MM-DD') AS LI_DT, A.ITDC_DT, /*도입일*/ A.ABRG_DT, /*폐기일*/ A.ETC_MTTR_CN FROM TBCSPIIT024M A WHERE 1=1 AND A.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND (UPPER(A.SW_MNG_CD) LIKE '%' ||UPPER(#strSEARCH_NM#)||'%' OR UPPER(A.SW_VERSION) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' OR UPPER(A.SW_MAKER) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' OR UPPER(A.SW_SERIALNO) LIKE '%' ||UPPER(#strSEARCH_NM#)||'%') AND TO_CHAR(TO_DATE(ITDC_DT),'YYYYMMDD') >= #strITDC_DTS# AND TO_CHAR(TO_DATE(ITDC_DT),'YYYYMMDD') AND TO_CHAR(TO_DATE(ABRG_DT),'YYYYMMDD') >= #strABRG_DTS# AND TO_CHAR(TO_DATE(ABRG_DT),'YYYYMMDD')