SELECT A.DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.MTRL_RQS_DVSN_CD,
    /*자료요청구분코드*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.RQSTR_ENO = Z.TRGT_NOP_ENO ) AS RQR_NAM, /*요청자명*/ A.RLS_YN, /*공개여부*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ A.PRCS_YMD, /*처리일*/ A.PRCS_PIC_ENO, /*처리자전산번호*/ (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M Z WHERE A.PRCS_PIC_ENO = Z.TRGT_NOP_ENO ) AS HNDL_PEN_NAM, /*처리자명*/ A.PRCS_CN, /*처리내용*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT030M Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '1' AND Z.COPUT_BRD_DVSN_CD = '01') AS DMND_DOC_ID, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT030M Z WHERE Z.DATA_DMND_SQNO = A.DATA_DMND_SQNO AND Z.COPUT_BRD_DVSN_CD = '01' AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '2') AS CRU_PRCS_DOC_ID, PRPS_YE, PRPS_SLN, F_DPT_INFO(A.DMND_DEPT_CD,'1') AS RQS_DPT_NM, (SELECT CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.RQSTR_ENO ) AS CRU_TELNO, NVL(SEN_YN,'N') AS SEN_YN, COPUT_BRD_DVSN_CD, A.PRCS_PIC_ENO AS OLD_HNDL_PEN_CNB ,A.HNDL_ORD_TXT ,A.DRTR_CNB /*책임자전산번호*/ ,A.DRTR_CNB AS OLD_DRTR_CNB ,(SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M ZZ WHERE A.DRTR_CNB = ZZ.TRGT_NOP_ENO ) AS DRTR_NAM, /*처리자명*/ CASE WHEN (A.PRCS_SECD = '0' OR A.PRCS_SECD IS NULL) THEN '미처리' ELSE '처리완료' END PRCS_SECD /*처리상태코드*/ FROM TBCSPIIT029M A WHERE A.DATA_DMND_SQNO = #DATA_DMND_SQNO# AND A.COPUT_BRD_DVSN_CD = '01' /*게시판구분코드 - BARON문의·개선요청*/