SELECT A.CD,
    A.CD_NM
FROM TBDCMACM013C A, TBDCMACM013C B WHERE A.CMM_CD_DVSN_CD = #strSecMnu# AND B.CMM_CD_DVSN_CD = #strFstMnu# AND A.CD NOT IN ('000000000') AND B.CD NOT IN ('000000000') AND B.USE_YN = 'Y' AND A.USE_YN = 'Y' AND A.USR_DFN_TXT_2 = B.USR_DFN_TXT_1||B.CD AND B.USR_DFN_TXT_1 = 'LV1' AND B.CD = #strFST_MNU_DVSN_CD#