SELECT F_CODE_NM('1000793',A.CSR_BRD_DVSN_CD) AS F_CODE_NM,
    /*게시판구분코드 */ F_CODE_NM('1000809',A.CSR_HNDL_WRK_DVSN_CD) AS TASK_SECD /* 업무구분코드 */,
    /*작업구분코드*/ A.CSR_RQS_DT AS DMND_YMD /* 요청일자 */,
    /*요청일*/ F_DPT_INFO(A.CSR_RQS_DPT_CD,'4') AS F_DPT_INFO,
    /*요청자부서명*/ F_USR_INFO(A.CSR_RQR_CNB,'2') AS F_USR_INFO,
    /*요청자명*/ A.CSR_RQS_TIT AS CSR_RQS_TIT,
    /*요청제목*/ F_USR_INFO(A.CSR_HNDL_CHGR_CNB,'2') AS F_USR_INFO,
    /*처리담당자명*/ A.CSR_SVC_BEGIN_DT AS GPCD_PRCS_YMD /* 처리일자 */,
    /*처리완료일자*/ A.CSR_MTRL_RQS_SNO AS DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.CSR_RQR_CNB AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ A.CSR_HNDL_CHGR_CNB AS PRCS_PIC_ENO /* 처리담당자직원전산번호 */,
    /*처리자전산번호*/ A.CSR_RQS_DPT_CD AS DMND_DEPT_CD /* 요청부서코드 */,
    /*요청부서코드*/ F_CODE_NM('1000794',A.CSR_REL_TSK_DVSN_CD) AS F_CODE_NM,
    /*관련업무구분코드*/ A.CSR_BRD_DVSN_CD AS CSR_BRD_DVSN_CD,
    F_CODE_NM('1000813',A.CSR_MNU_CD_1) AS F_CODE_NM,
    F_CODE_NM('1000814',A.CSR_MNU_CD_2) AS F_CODE_NM,
    CASE WHEN A.CSR_HNDL_YR IS NOT NULL THEN A.CSR_HNDL_YR||'-'||A.CSR_HNDL_WRK_SNO ELSE '' END AS CSR_HNDL_YR,
    A.CSR_HNDL_YR,
    A.CSR_HNDL_WRK_SNO,
    F_CODE_NM('1000333',NVL(A.CSR_APV_STA_CD,'1')) AS F_CODE_NM,
    A.CSR_STSFDG_OPRTN_YN,
    /*만족도 설문여부*/ F_CODE_NM('1001005',A.CSR_PRSS_STA_CD) AS F_CODE_NM,
    F_USR_INFO(A.CSR_RCEP_CNB,'2') AS F_USR_INFO,
    /*접수자명*/ A.CSR_ACP_DT AS CSR_ACP_DT,
    /*접수일자*/ F_CODE_NM('1001004',A.CSR_RQS_DVSN_CD) AS F_CODE_NM,
    F_CODE_NM('1001002',A.CSR_MNG_TSK_DVSN_CD) AS F_CODE_NM,
    F_CODE_NM('1001006',A.CSR_HNDL_DVSN_CD) AS F_CODE_NM,
    F_DPT_INFO(A.CSR_HNDL_DPT_CD,'4') AS PRCS_DEPT_NM /* 처리부서명 */,
    A.CSR_HNDL_APT_DT,
    F_CODE_NM('1000333',NVL(A.CSR_HNDL_APV_STA_CD,'1')) AS F_CODE_NM,
    /*처리결재상태*/ A.CSR_HNDL_APV_DOC_NO AS CRU_PRCS_ATRZ_DOC_NO /* 부패처리결재문서번호 */
FROM TBCSPIIT100M A WHERE A.CSR_BRD_DVSN_CD IN ('01','02') /*게시판구분코드 - 전산개선,자료요청*/ AND A.CSR_MNG_DVSN_CD = '04' /*유지관리구분코드 - 사이버보안요청*/ AND A.CSR_BRD_DVSN_CD = #strBRD_DVSN_CD# AND A.CSR_RQS_DVSN_CD = #strRQS_DVSN_CD# AND A.CSR_MNG_TSK_DVSN_CD = #strMNG_TSK_DVSN_CD# AND A.CSR_RQS_DT &gt;= #strRQS_DTS# AND A.CSR_RQS_DT &lt;= #strRQS_DTE# AND A.CSR_ACP_DT &gt;= #strRCEP_DTS# AND A.CSR_ACP_DT &lt;= #strRCEP_DTE# AND A.CSR_HNDL_DVSN_CD = #strHNDL_DVSN_CD# AND A.CSR_HNDL_WRK_DVSN_CD = #strWORK_DVSN_CD# AND A.CSR_STSFDG_OPRTN_YN = #strSTSFDG_OPRTN_YN# AND (F_DPT_INFO(A.CSR_RQS_DPT_CD,'1') LIKE '%'||#strSEARCH_NM#||'%' OR F_DPT_INFO(A.CSR_ACP_DPT_CD,'1') LIKE '%'||#strSEARCH_NM#||'%' OR F_DPT_INFO(A.CSR_HNDL_DPT_CD,'1') LIKE '%'||#strSEARCH_NM#||'%' OR A.CSR_RQS_TIT LIKE '%'||#strSEARCH_NM#||'%' OR A.CSR_RQS_TXT LIKE '%'||#strSEARCH_NM#||'%' OR A.CSR_HNDL_TXT LIKE '%'||#strSEARCH_NM#||'%') AND F_USR_INFO(A.CSR_RQR_CNB,'2') LIKE '%'||#strRQR_NAM#||'%' AND F_USR_INFO(A.CSR_HNDL_CHGR_CNB,'2') LIKE '%'||#strHNDL_PEN_NAM#||'%' AND F_DPT_INFO(A.CSR_HNDL_DPT_CD,'1') LIKE '%'||#strHNDL_DPT_NM#||'%' AND ( A.CSR_RQS_OPEN_YN = 'Y' OR A.CSR_RQR_CNB = #strCNB#) AND A.CSR_SVC_BEGIN_DT IS NULL AND A.CSR_PRSS_STA_CD > '01' AND A.CSR_PRSS_STA_CD = #strRESULT_CD# AND A.CSR_MNU_CD_1 = #strMNU_CD_1# AND A.CSR_MNU_CD_2 = #strMNU_CD_2# AND (A.CSR_SVC_BEGIN_DT &gt;= #strHNDL_DTS# OR A.CSR_SVC_BEGIN_DT IS NULL) AND (A.CSR_SVC_BEGIN_DT &lt;= #strHNDL_DTE# OR A.CSR_SVC_BEGIN_DT IS NULL) ORDER BY A.CSR_HNDL_YR DESC, A.CSR_HNDL_WRK_SNO DESC