SELECT A.CSR_BRD_DVSN_CD AS BBS_SECD,
    /*게시판구분 (개선,자료)*/ A.CSR_MTRL_RQS_SNO AS DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.CSR_MNG_DVSN_CD AS CSR_MNG_DVSN_CD,
    /*유지관리구분코드*/ A.CSR_MNG_TSK_DVSN_CD AS CSR_MNG_TSK_DVSN_CD,
    /*업무구분코드*/ A.CSR_DTS_TSK_DVSN_CD AS CSR_DTS_TSK_DVSN_CD,
    /*업무구분상세코드*/ A.CSR_RQS_DVSN_CD AS CSR_RQS_DVSN_CD,
    /*자료요청구분코드*/ A.CSR_PRSS_STA_CD AS PRGRS_STTS_SECD /* 진행상태구분코드 */,
    /*진행상태코드*/ F_CODE_NM('1001005',A.CSR_PRSS_STA_CD) AS F_CODE_NM,
    /*진행상태*/ A.CSR_RQS_DT AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.CSR_RQR_CNB AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ F_USR_INFO(A.CSR_RQR_CNB,'2') AS F_USR_INFO,
    /*요청자명*/ A.CSR_RQS_OPEN_YN AS RLS_YN /* 공개여부 */,
    /*공개여부*/ A.CSR_RQS_TIT AS CSR_RQS_TIT,
    /*요청제목명*/ A.CSR_RQS_TXT AS DMND_CN /* 요청내용 */,
    /*요청내용*/ A.CSR_SVC_BEGIN_DT AS GPCD_PRCS_YMD /* 처리일자 */,
    /*완료일*/ A.CSR_HNDL_CHGR_CNB AS CSR_HNDL_CHGR_CNB,
    /*처리자전산번호*/ F_USR_INFO(A.CSR_HNDL_CHGR_CNB,'2') AS F_USR_INFO,
    /*처리자명*/ A.CSR_HNDL_TXT AS PRCS_CN /* 처리내용 */,
    /*처리내용*/ (SELECT AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT009M Z WHERE Z.DATA_DMND_SQNO = A.CSR_MTRL_RQS_SNO AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '3' /*1(구)개선,2(구)자료,3개선,4자료*/ AND Z.COPUT_BRD_DVSN_CD = A.CSR_BRD_DVSN_CD) AS DMND_DOC_ID , /*요청첨부파일*/ (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT009M Z WHERE Z.DATA_DMND_SQNO = A.CSR_MTRL_RQS_SNO AND Z.COPUT_BRD_DVSN_CD = A.CSR_BRD_DVSN_CD AND Z.MTRL_RQS_ATT_FL_DVSN_CD = '4') AS CRU_PRCS_DOC_ID , /*처리첨부파일*/ A.CSR_PRPS_YR AS PRPS_YE, /*제안연도*/ A.CSR_PRPS_SLN AS PRPS_SLN, /*제안번호*/ F_DPT_INFO(A.CSR_RQS_DPT_CD,'1') AS RQS_DPT_NM, /*요청자부서*/ A.CSR_REL_TSK_DVSN_CD AS DGE1_MNU_DVSN_CD, /*관련업무구분코드*/ F_USR_INFO(A.CSR_RQR_CNB,'6') AS CRU_TELNO, /*내선번호*/ NVL(A.CSR_HNDL_RESULT_SEN_YN,'N') AS SEN_YN, /*요청결과메시지전송여부 */ A.CSR_HNDL_CHGR_CNB AS OLD_HNDL_CHGR_CNB, A.CSR_HNDL_YR AS WORK_YE, /*처리연도*/ A.CSR_HNDL_WRK_SNO AS WORK_SNO, /*처리순번*/ A.CSR_HNDL_WRK_DVSN_CD AS TASK_SECD, /*작업구분*/ A.CSR_MNU_CD_1 AS MNU_CD_1, /*1차메뉴*/ A.CSR_MNU_CD_2 AS MNU_CD_2, /*2차메뉴*/ A.CSR_DIST_DT AS DIST_DT, /*배포일*/ A.CSR_DIST_CHGR_CNB AS DIST_CHGR_CNB, /*배포자전산번호*/ F_USR_INFO(A.CSR_DIST_CHGR_CNB,'2') AS DIST_CHGR_NAM, /*배포자명*/ CASE WHEN A.CSR_HNDL_YR IS NOT NULL THEN A.CSR_HNDL_YR||'-'||A.CSR_HNDL_WRK_SNO ELSE '' END WORK_SRNO, /*작업번호*/ A.CSR_HNDL_WRK_DTL_TXT AS WORK_TXT, /*작업세부내용*/ A.CSR_HNDL_ORD_TXT AS HNDL_ORD_TXT, /*처리지시내용*/ A.CSR_RCEP_CNB AS RCEP_CNB, /*접수자전산번호*/ F_USR_INFO(A.CSR_RCEP_CNB,'2') AS RCEP_NAM, /*접수자명*/ A.CSR_ACP_DT AS NTFCTN_RCPT_YMD, /*접수일*/ A.CSR_RET_RSN_TXT AS RJCT_RSN_CN, /*보완필요사유*/ A.CSR_HNDL_DVSN_CD AS PRCS_SECD, /*SR처리구분*/ A.CSR_TSK_IPTC AS TSK_IPTC, /*업무중요도*/ A.CSR_TSK_DFFLY AS TSK_DFFLY, /*업무난이도*/ A.CSR_TSK_INFL AS TSK_INFL, /*업무영향도*/ A.CSR_HNDL_APT_DT AS HNDL_APT_DT, /*처리예정일*/ A.CSR_RQR_CFM_TXT AS RQR_CFM_TXT, /*요청확인내용*/ A.CSR_DIST_DVSN_CD AS DIST_DVSN_CD, /*배포구분코드*/ A.CSR_RM_TXT AS RM_TXT, /*비고*/ A.CSR_HWP_DOC AS HWP_DOC, /*별지서식*/ A.CSR_HNDL_APV_DOC_NO AS CRU_PRCS_ATRZ_DOC_NO, /*처리결재문서번호*/ A.CSR_APV_DOC_NO AS ATRZ_DOC_NO, /*요청결재문서번호*/ F_CODE_NM('1000333',NVL(A.CSR_HNDL_APV_STA_CD,'1')) AS HNDL_APV_STA_NM /*요청처리결재상태*/ FROM TBCSPIIT100M A WHERE A.CSR_MTRL_RQS_SNO = #strMTRL_RQS_SNO# AND A.CSR_BRD_DVSN_CD = #strBRD_DVSN_CD# /*게시판구분코드 - 개선요청01, 자료요청02*/