SELECT T1.CVLCPT_ADT_YR AS CVLCPT_ADT_YR /* 민원감사년도 */,
    T1.ADT_NO AS ADT_NO /* 감사번호 */,
    F_DPT_INFO(T2.SPRVSN_DEPT_CD,'1') AS F_DPT_INFO /* 감사부서 */,
    F_MNG_KND_AUDYRNO(T1.CVLCPT_ADT_YR, T1.ADT_NO, T2.ADT_KDCD, '-') || '-' || T1.FRS_MDM_SQNO AS ADT_CLSF_NO /* 감사분류번호 */,
    T2.ADT_MTTR_NM AS ADT_MTTR_NM /* 감사사항명 */,
    TO_CHAR(TO_DATE(T1.SBMSN_YMD),'YYYY-MM-DD') AS SBMSN_YMD /* 제출일자 */,
    T1.PRSNR_NM AS PRSNR_NM /* 제출자명 */,
    T1.SBMSN_NOCS AS SBMSN_NOCS /* 제출건수 */,
    T1.EDOC_PRCS_STP_SECD AS EDOC_PRCS_STP_SECD /* 소명자료처리단계구분코드 */,
    F_CODE_NM('1000722',T1.EDOC_PRCS_STP_SECD) AS F_CODE_NM /* 소명자료처리단계코드명 */,
    SUBSTR( DECODE(T1.EDOC_SBMSN_INTN_YN_1, 'Y', ',사실관계상이') || DECODE(T1.EDOC_SBMSN_INTN_YN_2, 'Y', ',새로운증거') || DECODE(T1.EDOC_SBMSN_INTN_YN_3, 'Y', ',법령해석 차이') || DECODE(T1.EDOC_SBMSN_INTN_YN_4, 'Y', ',적극행정면책') || DECODE(T1.EDOC_SBMSN_INTN_YN_5, 'Y', ',정상참작 등 기타') ,2) AS SUBSTR /* 소명자료제출취지여부명 */,
    T1.EDOC_SBMSN_INTN_YN_1 AS EDOC_SBMSN_INTN_YN_1 /* 소명자료제출취지여부1 */,
    T1.EDOC_SBMSN_INTN_YN_2 AS EDOC_SBMSN_INTN_YN_2 /* 소명자료제출취지여부2 */,
    T1.EDOC_SBMSN_INTN_YN_3 AS EDOC_SBMSN_INTN_YN_3 /* 소명자료제출취지여부3 */,
    T1.EDOC_SBMSN_INTN_YN_4 AS EDOC_SBMSN_INTN_YN_4 /* 소명자료제출취지여부4 */,
    T1.EDOC_SBMSN_INTN_YN_5 AS EDOC_SBMSN_INTN_YN_5 /* 소명자료제출취지여부5 */,
    T1.AGT_YN AS AGT_YN /* 대리인여부 */,
    F_CODE_NM('1000724',T1.AGT_YN) AS F_CODE_NM /* 대리인여부명 */,
    T1.AAI_APLY_YN AS AAI_APLY_YN /* 적극행정면책신청여부 */,
    DECODE(T1.AAI_APLY_YN,'Y','신청','미신청') AS AAI_APLY_YN /* 적극행정면책신청여부 */,
    T1.ITVW_APLY_YN AS ITVW_APLY_YN /* 면담신청여부 */,
    DECODE(T1.ITVW_APLY_YN,'Y','신청','미신청') AS ITVW_APLY_YN /* 면담신청여부 */,
    T1.ITVW_ENFRC_YN_SECD AS ITVW_ENFRC_YN_SECD /* 면담실시여부구분코드 */,
    F_CODE_NM('1000725',T1.ITVW_ENFRC_YN_SECD) AS F_CODE_NM /* 면담실시여부코드명 */,
    TO_CHAR(TO_DATE(T1.ENFC_YMD),'YYYY-MM-DD') AS ENFC_YMD /* 시행일자 */,
    T1.RCG_YN AS RCG_YN /* 인정여부 */,
    DECODE(T1.RCG_YN,'Y','인정','N','불인정','') AS RCG_YN /* 인정여부 */,
    T1.PTOUT_MTTR_CN AS PTOUT_MTTR_CN /* 지적사항내용 */,
    T1.RMRK_CN AS RMRK_CN /* 비고내용 */
FROM TBCSPKDE003M T1 /* 감사소명자료기본 */ ,TB_BADDED001M T2 /* 감사사항기본 */ WHERE 1=1 AND T1.CVLCPT_ADT_YR = T2.ADT_YR(+) AND T1.ADT_NO = T2.ADT_NO(+) AND T1.CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND T2.ADT_GRNT_NO = #ADT_GRNT_NO# AND SUBSTR(T2.ADT_KDCD,3,1) = SUBSTR(#strAudYrNoKndCd#,3,1) AND T1.SBMSN_YMD BETWEEN NVL(#strSmtDtSt#,'19000101') AND NVL(#strSmtDtEd#,'29991231') AND T1.EDOC_PRCS_STP_SECD = #EDOC_PRCS_STP_SECD# AND T2.ADT_MTTR_NM LIKE '%' || #strAudSbjNm# || '%' AND T1.PTOUT_MTTR_CN LIKE '%' || #strIndcSbj# || '%' AND T1.PRSNR_NM LIKE '%' || #strPrsr# || '%' AND T1.SBMSN_NOCS = #SBMSN_NOCS# AND T1.EDOC_SBMSN_INTN_YN_1 IS NOT NULL /* 사실관계상이 */ AND T1.EDOC_SBMSN_INTN_YN_2 IS NOT NULL /* 새로운증거 */ AND T1.EDOC_SBMSN_INTN_YN_3 IS NOT NULL /* 법령해석차이 */ AND T1.EDOC_SBMSN_INTN_YN_4 IS NOT NULL /* 적극행정면책 */ AND T1.EDOC_SBMSN_INTN_YN_5 IS NOT NULL /* 정상참작 등 기타 */ AND T1.AGT_YN = #AGT_YN# AND T1.AAI_APLY_YN = #AAI_APLY_YN# AND T1.ITVW_APLY_YN = #ITVW_APLY_YN# AND T1.ITVW_ENFRC_YN_SECD = #ITVW_ENFRC_YN_SECD# AND NVL(T1.ENFC_YMD,'19000101') BETWEEN NVL(#strEnfDtSt#,'19000101') AND NVL(#strEnfDtEd#,'29991231') AND T1.RCG_YN = #RCG_YN# ORDER BY T1.SBMSN_YMD, T2.SPRVSN_DEPT_CD