SELECT F_DPT_INFO(D1.CFM_DPT_CD,'1') AS F_DPT_INFO /* 확인부서 */,
    CASE WHEN D1.CFM_DT_1 IS NOT NULL THEN F_USR_INFO(D1.CFM_CNB_1,'2') || '['|| TO_CHAR(TO_DATE(D1.CFM_DT_1),'YYYY-MM-DD') || ']' ELSE '미확인' END AS CFM_DT_1 /* 과장확인내용 */,
    CASE WHEN D1.CFM_DT_2 IS NOT NULL THEN F_USR_INFO(D1.CFM_CNB_2,'2') || '['|| TO_CHAR(TO_DATE(D1.CFM_DT_2),'YYYY-MM-DD') || ']' ELSE '미확인' END AS CFM_DT_2 /* 담당확인내용 */,
    CASE WHEN D1.SVY_FACT_NTF_CNB IS NOT NULL THEN F_USR_INFO(D1.SVY_FACT_NTF_CNB,'2') || '[' || TO_CHAR(TO_DATE(D1.SVY_FACT_NTF_DT),'YYYY-MM-DD') || ']' ELSE '' END AS SVY_FACT_NTF_CNB /* 조사사실통보정보 */
FROM TBCSPKDE006L D1 ,TBDCMACM002M D2 WHERE D1.CFM_DPT_CD = D2.SRECS_DEPT_CD(+) AND D1.REGI_SRNO = #strRegiSrno# ORDER BY D2.DPT_SNO