SELECT D1.REGI_SRNO AS REGI_SRNO /* 일련번호 */,
    (SELECT AS ABR_DPT_NM_1
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = D1.REG_DEPT_CD) AS S_REGI_DPT_NM /* 약칭 등록부서명 */ ,D1.TPOF_OGDP_INST_NM || '(' || D1.CRU_OGDP_DEPT_NM || ')' AS L_BLT_ORG_DPT_NM /* 목록 소속기관부서명 */ ,D1.JBGD_NM AS JBGD_NM /* 직급명 */ ,D1.INFO_RLPR_NM AS INFO_RLPR_NM /* 성명 */ ,D1.CRU_RRNO_SCRTY AS CRU_RRNO_SCRTY /* 암호화주민등록번호 */ ,D1.CFM_DDLN_DT AS CFM_DDLN_DT /* 확인기한일 */ ,D2.SVY_FACT_NTF_DT AS SVY_FACT_NTF_DT /* 조사사실통보일자 */ ,D2.CFM_DPT_CD AS CFM_DPT_CD /* 확인부서 */ ,(SELECT ABR_DPT_NM_1
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = D2.CFM_DPT_CD) AS CFM_DPT_NM /* 확인부서 명*/ ,CASE WHEN D2.CFM_DT_1 IS NULL THEN 'N' ELSE 'Y' END AS CFM_1_YN /* 과장확인여부 */ ,CASE WHEN D2.CFM_DT_2 IS NULL THEN 'N' ELSE 'Y' END AS CFM_2_YN /* 담당확인여부 */ ,CASE WHEN D1.CVLCPT_DOC_ID IS NOT NULL THEN '파일첨부' ELSE D1.CRU_RRNO_SCRTY END AS FILE_ADD_NM /* 파일첨부 */ ,D1.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 문서ID */ FROM TBCSPKDE005M D1 /* 의원면직기본 */ ,TBCSPKDE006L D2 /* 의원면직확인내역 */ WHERE D2.CFM_DPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND D1.REGI_SRNO = D2.REGI_SRNO AND D2.CFM_DPT_CD = #strDptCd2# AND D1.REG_YMD BETWEEN #strDtStr# AND #strDtEnd# AND D1.TPOF_OGDP_INST_NM LIKE '%' || #strOrgNm# || '%' AND D1.INFO_RLPR_NM LIKE '%' || #strNam# || '%' AND NOT EXISTS (SELECT 1
FROM TBCSPKDE006L WHERE REGI_SRNO = D1.REGI_SRNO AND CFM_DPT_CD = D2.CFM_DPT_CD AND (CFM_DT_1 IS NULL OR CFM_DT_2 IS NULL)) AND EXISTS (SELECT 1
FROM TBCSPKDE006L WHERE REGI_SRNO = D1.REGI_SRNO AND CFM_DPT_CD = D2.CFM_DPT_CD AND (CFM_DT_1 IS NULL OR CFM_DT_2 IS NULL)) AND CFM_DT_1 IS NULL) AND CFM_DT_2 IS NULL)