SELECT A.LON_DDCT_DVSN_CD,
    C.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    A.LON_PEN_CNB,
    B.USER_NM /* 사용자명 */,
    SUBSTR(B.CRU_RRNO_SCRTY,0, 6) || (SELECT TO_CHAR('-*******') AS CRU_RRNO_SCRTY /* 부패주민등록번호보안 */
FROM DUAL) AS CRU_RRNO_SCRTY, A.TOT_LON_AM, A.MN_STT_AM, A.STR_YM, A.END_YM, SUBSTR(#yyyymm#,0,4)||'-'||SUBSTR(#yyyymm#,5,6) AS CVLCPT_RCPT_YM FROM TBCSPLFI001M A, TBDCMACM001M B, TBDCMACM002M C WHERE 1=1 AND A.LON_PEN_CNB = B.TRGT_NOP_ENO AND B.SRECS_DEPT_CD = C.SRECS_DEPT_CD AND A.STR_YM = #yyyymm# AND A.LON_DDCT_DVSN_CD = #lonDdctDvsnCd# AND A.LON_PEN_CNB = #cnb# AND B.USER_NM LIKE '%' || #name# || '%' ORDER BY SUBSTR(B.CRU_RRNO_SCRTY,0, 6), A.LON_DDCT_DVSN_CD, C.CNSTN_MBCMT_DEPT_NM, A.LON_PEN_CNB