SELECT TO_CHAR(SUM(A.MN_STT_AM), 'FM999,999,999,999,999,999,999,999') AS TO_CHAR
FROM TBCSPLFI001M A, TBDCMACM001M B, TBDCMACM002M C WHERE 1=1 AND A.LON_PEN_CNB = B.TRGT_NOP_ENO AND B.SRECS_DEPT_CD = C.SRECS_DEPT_CD AND A.STR_YM &lt;= #yyyymm# AND A.END_YM &gt;= #yyyymm# AND A.LON_DDCT_DVSN_CD = #lonDdctDvsnCd# AND A.LON_PEN_CNB = #cnb# AND B.USER_NM LIKE '%' || #name# || '%' ORDER BY A.LON_DDCT_DVSN_CD, C.CNSTN_MBCMT_DEPT_NM, A.LON_PEN_CNB