SELECT F_USR_INFO(A.LON_PEN_CNB, '2') AS F_USR_INFO,
    A.LON_PEN_CNB,
    C.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    SUBSTR(B.CRU_RRNO_SCRTY,0, 6) || (SELECT TO_CHAR('-*******') AS CRU_RRNO_SCRTY /* 부패주민등록번호보안 */
FROM DUAL) AS CRU_RRNO_SCRTY, A.LON_DDCT_DVSN_CD, F_CODE_NM('1000176',B.JBPS_CD) AS JBPS_NM, F_CODE_NM('1000173',B.JBGD_SECD) AS JBGD_NM, '' AS HOBONG, A.LON_DDCT_SRNO, A.TOT_LON_AM, A.MN_STT_AM, A.STR_YM, A.END_YM, CASE WHEN A.END_YM <= (SELECT TO_CHAR(SYSDATE, 'YYYYMM')
FROM DUAL) THEN 'Y' ELSE 'N' END AS YN, A.LON_DDCT_SRNO FROM TBCSPLFI001M A, TBDCMACM001M B, TBDCMACM002M C WHERE 1=1 AND A.LON_PEN_CNB = B.TRGT_NOP_ENO AND B.SRECS_DEPT_CD = C.SRECS_DEPT_CD AND A.LON_PEN_CNB = #cnb# ORDER BY A.LON_DDCT_DVSN_CD, C.CNSTN_MBCMT_DEPT_NM, A.LON_PEN_CNB