SELECT (SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000166' AND CD = B.PROF_RBPRSN_BANK_KDCD) AS PROF_RBPRSN_BANK_KDCD, B.ACTNO_SCRTY AS ACTNO_SCRTY, (SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000145' AND CD = A.ALW_DVSN_CD) AS ALW_DVSN_CD, A.ALW_AM, A.TRGT_NOP_ENO, F_DPT_INFO(SUBSTR(C.SRECS_DEPT_CD, 0,3)||'000', 1) AS BRU, F_DPT_INFO(C.SRECS_DEPT_CD, 1) AS CNSTN_MBCMT_DEPT_NM, C.USER_NM, F_CODE_NM('1000173', C.JBGD_SECD) AS RANKCD, F_CODE_NM('1000176', C.JBPS_CD) AS PSTCD FROM TBCSPLFI102M A, TBCSPLFI103M B, TBDCMACM001M C WHERE 1=1 AND A.TRGT_NOP_ENO = C.TRGT_NOP_ENO AND A.TRGT_NOP_ENO = B.TRGT_NOP_ENO AND C.BAI_USR_YN <> 'N' AND C.SRECS_DEPT_CD <> '880880' AND A.ALW_AM IS NOT NULL AND A.ALW_AM <> 0 AND A.ALW_DVSN_CD IN ( SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #cd# ||',' AS #CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) ORDER BY B.PROF_RBPRSN_BANK_KDCD, A.ALW_DVSN_CD, A.TRGT_NOP_ENO