MERGE INTO TBCSPLFI102M A USING ( select #TRGT_NOP_ENO# AS TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    #ALW_DVSN_CD# AS #ALW_DVSN_CD#
from DUAL ) B ON (A.TRGT_NOP_ENO = B.TRGT_NOP_ENO AND A.ALW_DVSN_CD = B.ALW_DVSN_CD) WHEN MATCHED THEN UPDATE SET A.ALW_AM = #ALW_AM#,  A.FIX_YN = #FIX_YN#,  A.LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  A.LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  A.LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  A.LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE 1=1 AND A.ALW_DVSN_CD = #ALW_DVSN_CD# AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO# WHEN NOT MATCHED THEN INSERT INSERT ( A.ALW_DVSN_CD ,A.TRGT_NOP_ENO ,A.ALW_AM ,A.FIX_YN ,A.FRST_USER_ID ,A.LAST_USER_ID ,A.LAST_PRCS_DEPT_CD ,A.LAST_MENU_ID ,A.FRST_REG_DT ,A.LAST_MDFCN_DT ) VALUES ( #ALW_DVSN_CD# ,#TRGT_NOP_ENO# ,#ALW_AM# ,#FIX_YN# ,#FRT_USR_ID# ,#LAST_USER_ID# ,#LAST_PRCS_DEPT_CD# ,#LAST_MENU_ID# ,SYSDATE ,SYSDATE )