MERGE INTO TBCSPLFI103M A USING ( select #cnb# AS TRGT_NOP_ENO /* 대상인원직원전산번호 */
from DUAL ) B ON (A.TRGT_NOP_ENO = B.TRGT_NOP_ENO) WHEN MATCHED THEN UPDATE SET A.PROF_RBPRSN_BANK_KDCD = #PROF_RBPRSN_BANK_KDCD# /* 증명책임자은행종류코드 */,  A.ACTNO_SCRTY = #ACTNO_SCRTY# /* 계좌번호보안 */,  A.DPSTR_NM = #DPSTR_NM# /* 예금주명 */,  A.LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  A.LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  A.LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  A.LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT (TRGT_NOP_ENO, PROF_RBPRSN_BANK_KDCD, ACTNO_SCRTY, DPSTR_NM, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) VALUES (#cnb#, #PROF_RBPRSN_BANK_KDCD#, #ACTNO_SCRTY#, #DPSTR_NM#, #FRT_USR_ID#, #LAST_USER_ID#, #LAST_PRCS_DEPT_CD#, #LAST_MENU_ID#, SYSDATE, SYSDATE) /* 최종수정일시 */