SELECT NVL(E.ABR_DPT_NM_2,'') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /* 20181104 EUNOK D.CD_NM -> E.ABR_DPT_NM_2 */ B.USER_NM || '(' || A.TRGT_NOP_ENO || ')' AS USER_NM /* 사용자명 */,
    A.AMT1,
    #toYm# AS #TOYM#
FROM ( SELECT TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    SRECS_DEPT_CD /* 심사재심의부서코드 */,
    NVL(MAX(CASE WHEN B.ALW_DVSN_CD = #AlwDvsnCd# THEN B.PMT_AM END),0) AS ALW_DVSN_CD
FROM TBCSPLFI104L B WHERE PMT_YM = #toYm# GROUP BY TRGT_NOP_ENO ,SRECS_DEPT_CD ) A, TBDCMACM001M B, TBDCMACM013C C, TBDCMACM013C D, TBDCMACM002M E WHERE A.TRGT_NOP_ENO = B.TRGT_NOP_ENO AND A.SRECS_DEPT_CD = D.CD AND C.CMM_CD_DVSN_CD = '1000173' AND C.CD = B.JBGD_SECD AND D.CMM_CD_DVSN_CD = '1000144' AND D.CD = A.SRECS_DEPT_CD AND A.SRECS_DEPT_CD = E.SRECS_DEPT_CD AND B.USER_NM LIKE #name# || '%' AND A.SRECS_DEPT_CD LIKE '%' || #dptCd# || '%' AND AMT1 != 0 ORDER BY E.DPT_SNO ASC, E.CRU_SORT_SQNO ASC, A.SRECS_DEPT_CD ASC, B.INSD_JBGD_SECD ASC, C.USR_DFN_TXT_3 ASC, B.NOW_JBGD_APNTMN_YMD ASC