SELECT DECODE(A.NTFCTN_RCPT_SQNO,NULL,'',A.ACP_YE||'-'||A.NTFCTN_RCPT_SQNO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    CASE WHEN NAM_OPEN_YN = 'Y' THEN F_USR_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.TRGT_NOP_ENO) ,'2') ELSE '익명' END AS USER_NM /* 사용자명 */,
    A.NAM_OPEN_YN,
    CASE WHEN NAM_OPEN_YN = 'Y' THEN F_DPT_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.SRECS_DEPT_CD),'1') ELSE '' END AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    A.SBMSN_YMD /* 제출일자 */,
    A.EXAM_DPT_CD /* 검토부서코드 */,
    CASE WHEN NVL(A.MDT_YN,'N') = 'Y' AND A.PRGRS_STTS_SECD IN ('M', 'N') THEN F_DPT_INFO(A.MDT_DPT_CD, 1) ELSE F_DPT_INFO(A.EXAM_DPT_CD, 1) END AS MDT_YN,
    A.RLS_YMD /* 공개일자 */,
    60 - (TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) - TO_DATE(A.RLS_YMD)) AS 60 /* 숨김일자 */,
    A.PRGRS_STTS_SECD /* 진행상태구분코드 */,
    F_CODE_NM('1000992',A.PRGRS_STTS_SECD) AS F_CODE_NM,
    A.TK_YE /* 생각연도 */,
    A.TK_SLN /* 생각연번 */,
    (SELECT Z.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPLTT002M Z WHERE Z.TK_YE = A.TK_YE AND Z.TK_SLN = A.TK_SLN AND Z.ATT_FL_DVSN_CD = '1') AS CVLCPT_DOC_ID /*첨부파일 문서*/ ,A.EXAM_DT ,NVL(A.ENAIS_PST_INQ_NMTM, 0) AS ENAIS_PST_INQ_NMTM ,NVL(B.CMMT_CNT, 0) AS CMMT_CNT ,NVL(B.CMMT_PED_CNT, 0) AS CMMT_PED_CNT ,NVL(C.RCMD_CNT, 0) AS RCMD_CNT ,NVL(C.DCMD_CNT, 0) AS DCMD_CNT , CASE WHEN TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) 0 THEN 'Y' ELSE 'N' END AS CMT_NEW_YN , CASE WHEN NVL(A.MDT_YN,'N') = 'Y' AND A.PRGRS_STTS_SECD IN ('M', 'N') AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) 'Y' AND A.PRGRS_STTS_SECD IN ('M', 'N') AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) AND A.PRGRS_STTS_SECD &gt;= 'B' AND A.PRGRS_STTS_SECD = #PRGRS_STTS_SECD# AND (A.SBMSN_YMD &gt;= #strSMT_DTS# OR A.SBMSN_YMD IS NULL) AND (A.SBMSN_YMD &lt;= #strSMT_DTE# OR A.SBMSN_YMD IS NULL) AND DGUARD.DECRYPT('TB_ENC','EAN', A.TRGT_NOP_ENO) = #strCNB# AND A.NAM_OPEN_YN = 'Y' AND A.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' ORDER BY TO_NUMBER(A.TK_YE) DESC , TO_NUMBER(A.NTFCTN_RCPT_SQNO) DESC