UPDATE TBCSPLTT001M SET RLS_YMD = TO_CHAR(SYSDATE,'YYYYMMDD') /*제출일자*/ , RLS_YN = 'Y' /*글 공개여부*/ , PRGRS_STTS_SECD = 'D' /*진행상태 = D : 공개*/ , ACP_YE = TO_CHAR(SYSDATE, 'YYYY') , NTFCTN_RCPT_SQNO = (SELECT TO_CHAR(NVL(MAX(TO_NUMBER(NTFCTN_RCPT_SQNO))+1,1)) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */
FROM TBCSPLTT001M WHERE ACP_YE = TO_CHAR(SYSDATE, 'YYYY') ) , LAST_MDFCN_DT = SYSDATE /*최종수정일시*/ WHERE TK_YE = #TK_YE# AND TK_SLN = #TK_SLN#