SELECT DECODE(A.NTFCTN_RCPT_SQNO,NULL,'',TK_YE||'-'||A.NTFCTN_RCPT_SQNO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    F_USR_INFO(A.TRGT_NOP_ENO,'2') AS USER_NM /* 사용자명 */,
    TO_CHAR(TO_DATE(A.SBMSN_YMD),'YYYY-MM-DD') AS SBMSN_YMD /* 제출일자 */,
    F_CODE_NM('1000992', A.PRGRS_STTS_SECD) AS F_CODE_NM /* 진행상태코드 */,
    F_DPT_INFO(A.EXAM_DPT_CD,'1') AS F_DPT_INFO
FROM TBCSPLTT001M A /*함께 생각합시다 기본*/ WHERE 1=1 AND A.PRGRS_STTS_SECD &gt;= 'B' AND A.PRGRS_STTS_SECD = #PRGRS_STTS_SECD# AND (A.SBMSN_YMD &gt;= #strSMT_DTS# OR A.SBMSN_YMD IS NULL) AND (A.SBMSN_YMD &lt;= #strSMT_DTE# OR A.SBMSN_YMD IS NULL) AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND A.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' ORDER BY TO_NUMBER(A.TK_YE) DESC , TO_NUMBER(A.NTFCTN_RCPT_SQNO) DESC