SELECT /* csp.ltt.service.impl.CSPLTT006EServiceImpl.CSPLTT006ECommentSelect */ ROW_NUMBER() OVER (ORDER BY A.TK_YE, A.TK_SLN, A.CMMT_SEQ) AS NO , A.TK_YE /* 생각연도 */ , A.TK_SLN /* 생각연번 */ , A.CMMT_SEQ /* 생각연번 */ , CASE WHEN NAM_OPEN_YN = 'Y' THEN F_USR_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.CRE_CNB), 2) WHEN NAM_OPEN_YN = 'N' THEN '익명' ELSE '익명' END AS CRE_NM /* 등록자명 */ , CASE WHEN NAM_OPEN_YN = 'Y' THEN F_DPT_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.CRE_USR_DPT_CD), 1) WHEN NAM_OPEN_YN = 'N' THEN '' ELSE '' END AS CRE_USR_DPT_NM /* 부서명 */ , A.NAM_OPEN_YN /* 실명공개여부 */ , A.CONTENT /* 댓글내용 */ , TO_CHAR(A.FRST_REG_DT, 'YYYY-MM-DD HH24:MI') AS FRST_REG_DT /* 등록일 */ , A.DEL_YN /* 삭제여부 */ , A.DEL_RSN /* 삭제사유 */ , CASE WHEN DGUARD.DECRYPT('TB_ENC','EAN', A.CRE_CNB) = #S_CNB# THEN 'Y' ELSE 'N' END AS WR_YN , CASE WHEN TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) AND A.CMMT_SEQ = #strCMMT_SEQ# ORDER BY A.TK_YE, A.TK_SLN, A.CMMT_SEQ