SELECT DECODE(A.NTFCTN_RCPT_SQNO,NULL,'',A.TK_YE||'-'||A.NTFCTN_RCPT_SQNO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.SBMSN_YMD /* 제출일자 */,
    A.RLS_YMD /* 공개일자 */,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    CASE WHEN A.NAM_OPEN_YN = 'Y' THEN F_USR_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.TRGT_NOP_ENO),'2') ELSE '익명' END AS USER_NM /* 사용자명 */,
    CASE WHEN A.NAM_OPEN_YN = 'Y' THEN F_DPT_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.SRECS_DEPT_CD),'1') ELSE '' END AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    A.NAM_OPEN_YN /* 성명공개여부 */,
    F_CODE_NM('1000992',A.PRGRS_STTS_SECD) AS F_CODE_NM /* 진행단계 */,
    A.PRGRS_STTS_SECD /* 진행상태구분코드 */,
    A.PRB_TXT /* 문제점내용 */,
    A.WSH_IMP_TXT /* 희망 개선내용 */,
    (SELECT Z.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPLTT002M Z WHERE Z.TK_YE = A.TK_YE AND Z.TK_SLN = A.TK_SLN AND Z.ATT_FL_DVSN_CD = '1') AS CVLCPT_DOC_ID /*첨부파일 문서*/ ,F_DPT_INFO(A.EXAM_DPT_CD,'1') AS EXAM_DPT_NM /*검토부서*/ ,A.EXAM_DPT_CD /*검토부서코드*/ ,A.EXAM_DT /*검토일자*/ ,F_USR_INFO(A.EXAM_CNB,'2') AS EXAM_NAM /*검토자*/ ,A.EXAM_CNB ,A.EXAM_TXT /*검토의견*/ ,A.EXAM_RSLT_CD /*검토결과*/ ,(SELECT Z.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPLTT002M Z WHERE Z.TK_YE = A.TK_YE AND Z.TK_SLN = A.TK_SLN AND Z.ATT_FL_DVSN_CD = '2') AS EXAM_DOC_ID /*검토첨부파일 문서*/ ,A.MDT_YN /*이견조정여부*/ ,F_DPT_INFO(A.MDT_DPT_CD,'1') AS MDT_DPT_NM /*검토부서*/ ,A.MDT_DPT_CD /*검토부서코드*/ ,A.MDT_DT /*검토일자*/ ,F_USR_INFO(A.MDT_CNB,'2') AS MDT_NAM /*검토자*/ ,A.MDT_CNB ,A.MDT_TXT /*검토의견*/ ,A.MDT_RSLT_CD /*검토결과*/ ,(SELECT Z.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPLTT002M Z WHERE Z.TK_YE = A.TK_YE AND Z.TK_SLN = A.TK_SLN AND Z.ATT_FL_DVSN_CD = '3') AS MDT_DOC_ID /*검토첨부파일 문서*/ ,A.TK_YE /*제안연도*/ ,A.TK_SLN /*제안연번*/ ,A.EXAM_APV_DOC_NO /*검토결재문서번호*/ ,A.EXEC_APT_DT /*검토 후 실시예정일*/ ,A.MDT_APV_DOC_NO ,A.MDT_EXEC_APT_DT ,A.CHOS_DCS_DT /*채택결정일*/ ,A.ENAIS_PST_INQ_NMTM /*조회수*/ ,(SELECT COUNT(*)
FROM TBCSPLTT003M B WHERE B.TK_YE = A.TK_YE AND B.TK_SLN = A.TK_SLN AND B.RCMD_YN = 'Y') AS RCMD_CNT /*추천수*/ ,(SELECT COUNT(*)
FROM TBCSPLTT003M B WHERE B.TK_YE = A.TK_YE AND B.TK_SLN = A.TK_SLN AND B.RCMD_YN = 'N') AS DCMD_CNT /*비추천수*/ ,(SELECT COUNT(*)
FROM TBCSPLTT004M B WHERE B.TK_YE = A.TK_YE AND B.TK_SLN = A.TK_SLN) AS CMMT_CNT /*댓글수*/ FROM TBCSPLTT001M A WHERE 1=1 AND A.TK_YE = #strTK_YE# AND A.TK_SLN = #strTK_SLN# AND ((MDT_APV_DOC_NO IS NOT NULL AND MDT_APV_DOC_NO = #strDOC_NO#) OR (EXAM_APV_DOC_NO IS NOT NULL AND EXAM_APV_DOC_NO = #strDOC_NO#))