SELECT TK_YE,
    TK_SLN,
    CASE WHEN MDT_YN = 'Y' THEN (SELECT AS MDT_YN
FROM TBCSPLTT001M WHERE TK_YE = #strTK_YE# AND TK_SLN = #strTK_SLN# AND NAM_OPEN_YN = 'Y') ELSE (SELECT EXAM_RPL_TXT
FROM TBCSPLTT001M WHERE TK_YE = #strTK_YE# AND TK_SLN = #strTK_SLN# AND NAM_OPEN_YN = 'Y') END RPL_TXT, MDT_YN, EXAM_RSLT_CD, MDT_RSLT_CD, PRGRS_STTS_SECD, RSCH_ASMT_TTL, SUBSTR(EXEC_APT_DT,3,2)||'. '||SUBSTR(EXEC_APT_DT,5,2)||'. '||SUBSTR(EXEC_APT_DT,7,2) AS EXEC_APT_DT, SUBSTR(MDT_EXEC_APT_DT,3,2)||'. '||SUBSTR(MDT_EXEC_APT_DT,5,2)||'. '||SUBSTR(MDT_EXEC_APT_DT,7,2) AS MDT_EXEC_APT_DT FROM TBCSPLTT001M WHERE 1=1 AND TK_YE = #strTK_YE# AND TK_SLN = #strTK_SLN# ORDER BY TO_NUMBER(TK_YE) DESC , TO_NUMBER(TK_SLN) DESC