SELECT /* csp.ltt.service.impl.CSPLTT014EServiceImpl.CSPLTT014EMainSelect */ A.TK_YE /* 생각연도 */,
    A.TK_SLN /* 생각연번 */,
    CASE WHEN NAM_OPEN_YN = 'N' THEN '익명' WHEN DGUARD.DECRYPT('TB_ENC','EAN', A.TRGT_NOP_ENO) = '3223' THEN '' /* 요청에 의한 하드코딩 이름숨김 */ WHEN TK_YE = '2022' AND TK_SLN IN ('1','25','38') THEN '' /* 요청에 의한 하드코딩 이름숨김 */ WHEN NAM_OPEN_YN = 'Y' THEN F_USR_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.TRGT_NOP_ENO), 2) ELSE '익명' END AS USER_NM /* 사용자명 */,
    CASE WHEN NAM_OPEN_YN = 'Y' THEN F_DPT_INFO(DGUARD.DECRYPT('TB_ENC','EAN', A.SRECS_DEPT_CD), 1) WHEN NAM_OPEN_YN = 'N' THEN '' ELSE '' END AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    A.SBMSN_YMD /* 제출일자 */,
    A.ACP_YE || A.NTFCTN_RCPT_SQNO AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.RLS_YN /* 공개여부 */,
    A.RLS_YMD /* 공개일자 */,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    (SELECT COUNT(*)
FROM TBCSPLTT003M WHERE TK_YE = A.TK_YE AND TK_SLN = A.TK_SLN AND RCMD_YN = 'Y') AS RCMR_CNT /* 추천수 */ , (SELECT COUNT(*)
FROM TBCSPLTT003M WHERE TK_YE = A.TK_YE AND TK_SLN = A.TK_SLN AND RCMD_YN = 'N') AS DCMR_CNT /* 비추천수 */ , ENAIS_PST_INQ_NMTM /* 조회수 */ , (SELECT COUNT(*)
FROM TBCSPLTT004M WHERE TK_YE = A.TK_YE AND TK_SLN = A.TK_SLN) AS CMMT_CNT /* 댓글수 */ , A.PRB_TXT /* 문제점내용 */ , CASE WHEN NVL(A.MDT_YN,'N') = 'Y' AND A.PRGRS_STTS_SECD IN ('M', 'N') THEN A.MDT_TXT WHEN NVL(A.MDT_YN,'N') = 'Y' AND A.PRGRS_STTS_SECD IN ('P', 'G', 'H', 'I', 'J', 'K') THEN A.EXAM_TXT WHEN NVL(A.MDT_YN,'N') <> 'Y' AND A.PRGRS_STTS_SECD IN ('M', 'N') THEN A.EXAM_TXT ELSE '' END AS EXAM_TXT /* 검토내용 */ , (SELECT NVL2(DGUARD.DECRYPT('TB_ENC', 'EAN', RCMR_CNB),'Y','N') AS ACTNO_SCRTY /* 계좌번호보안 */
FROM TBCSPLTT003M WHERE TK_YE = A.TK_YE AND TK_SLN = A.TK_SLN AND DGUARD.DECRYPT('TB_ENC', 'EAN', RCMR_CNB) = #S_CNB#) AS RCMD_YN /* 추천확인 */ , (SELECT Z.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPLTT002M Z WHERE Z.TK_YE = A.TK_YE AND Z.TK_SLN = A.TK_SLN AND Z.ATT_FL_DVSN_CD = '1') AS CVLCPT_DOC_ID , DECODE(SIGN(60 - (TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) - TO_DATE(A.RLS_YMD))),'1','Y','N') AS BTN_ENB_YN FROM TBCSPLTT001M A WHERE A.TK_YE = #strTkYe# AND A.TK_SLN = #strTkSln#