SELECT TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    USER_ID /* 사용자아이디 */,
    USER_NM /* 사용자명 */,
    (SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD ='1000173' AND CD = A.JBGD_SECD) AS JBGD_NM, (SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD ='1000176' AND CD = A.JBPS_CD )AS JBPS_NM, (SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = A.SRECS_DEPT_CD) AS CNSTN_MBCMT_DEPT_NM, '추가' AS NEW FROM TBDCMACM001M A WHERE 1=1 AND A.BAI_USR_YN = 'Y' AND A.SRECS_DEPT_CD != '880880' AND A.RTRM_YMD IS NULL AND (A.HDOF_SECD IS NULL OR A.HDOF_SECD != 'DAA') AND A.RQS_STA_CD = '3' AND A.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND A.SRECS_DEPT_CD LIKE #kuk#||'%' AND A.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND A.USER_NM LIKE '%'|| #usrNam# ||'%' ORDER BY TRGT_NOP_ENO