INSERT INTO TBCSPMWF003M (MEAL_DT, BOKG_ID, BOKG_PEN_CNB, BOKG_DT, MEAL_PEN_CNB, BOKG_YN, MEAL_YN, BOKG_CNL_YN, MEAL_AM, BOKG_REGI_DH, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) SELECT #ymd# AS #YMD#,
    (SELECT 'RS'|| TO_CHAR(SYSTIMESTAMP, 'YYYYMMDDHHMMSSFF4')
FROM DUAL) AS BOKG_ID, (SELECT TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBDCMACM001M WHERE USER_ID = A.FRST_USER_ID) AS BOKG_PEN_CNB, #ymd# AS BOKG_DT, A.TRGT_NOP_ENO AS MEAL_PEN_CNB, 'N', 'N', 'N', NULL, SYSDATE, A.FRST_USER_ID, A.LAST_USER_ID, A.LAST_PRCS_DEPT_CD, A.LAST_MENU_ID, SYSDATE, SYSDATE FROM TBCSPMWF005M A WHERE 1=1 AND A.TRGT_NOP_ENO NOT IN (SELECT MEAL_PEN_CNB
FROM TBCSPMWF003M WHERE MEAL_DT = #ymd#)