SELECT SUBSTR(EXTRACT(XMLAGG(XMLELEMENT(A, ', '|| HLDY_INFO_TXT ) ORDER BY HLDY_INFO_TXT), '//text()'), 3) AS SUBSTR
FROM ( SELECT '(' || TO_CHAR(TO_DATE(T2.HLDY), 'YYYY-MM-DD') || ') ' || T2.HLDY_RSN_TXT AS TO_CHAR
FROM (SELECT TO_CHAR(LEVEL + 2 + (SELECT NEXT_DAY(TO_DATE(#strStdDt#), 6) - 7
FROM DUAL), 'YYYYMMDD') AS DT FROM DUAL CONNECT BY LEVEL <= 5) T1 ,TBCSPMWF011M T2 WHERE BOKG_DVSN_CD = '3' /* 예약구분코드(1:한방,2:안마,3:치과) */ AND T1.DT = T2.HLDY )