    D1.LEND_KND_CD /* , F_CODE_NM( '1000330', D1.LEND_KND_CD ) AS LEND_KND_CD_NM */,
    ( SELECT D2.CD_NM
FROM TBDCMACM013C D2 WHERE D2.CMM_CD_DVSN_CD = '1000330' AND D2.CD = D1.LEND_KND_CD ) AS LEND_KND_CD_NM , D1.LEND_ITR , D1.ARR_ITR , D1.PIA_DVD_RTS , ( CASE WHEN ( SELECT COUNT(D2.LEND_SLN) AS LEND_SLN
