SELECT /* CSPNBK211EMouiOrgListSelect */ D1.MOUI_NO,
    D1.TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    D1.OGNZ_NM,
    ( SELECT D4.MOUI_NO
FROM TBCSPNBK001M D4 WHERE D4.MOUI_DVSN_CD = '0' AND D4.TRGT_NOP_ENO = D1.TRGT_NOP_ENO ) AS USR_MOUI_NO , D2.USER_NM , SUBSTR(D2.CRU_RRNO_SCRTY, 1, 6) || '-*******' AS CRU_RRNO_SCRTY , D2.JBGD_SECD , ( SELECT D5.CD_NM
FROM TBDCMACM013C D5 WHERE D5.CMM_CD_DVSN_CD = '1000173' AND D5.CD = D2.JBGD_SECD ) AS RANK_CD_NM , D3.CNSTN_MBCMT_DEPT_NM , D1.EX_DVD_RTS_TG_YN , D1.SAL_SEI_YN , D1.SCS_YN , D1.RMRK_CN , D1.RGR_YN FROM TBCSPNBK001M D1 , TBDCMACM001M D2 , TBDCMACM002M D3 WHERE D1.TRGT_NOP_ENO = D2.TRGT_NOP_ENO AND D2.SRECS_DEPT_CD = D3.SRECS_DEPT_CD AND D1.MOUI_DVSN_CD = '1' D1.MOUI_NO = #mouiNo# D1.TRGT_NOP_ENO = #TRGT_NOP_ENO# D1.OGNZ_NM LIKE '%' || #mouiNm# || '%' D1.SCS_YN = #scsYn# ORDER BY MOUI_NO