SELECT /* CSPNBK211EMouiPersonListSelect */ D1.MOUI_NO,
    D1.TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    D2.USER_NM /* 사용자명 */,
    SUBSTR(D2.CRU_RRNO_SCRTY, 1, 6) || '-*******' AS CRU_RRNO_SCRTY /* 부패주민등록번호보안 */,
    D2.JBGD_SECD /* 직급구분코드 */,
    ( SELECT D4.CD_NM
FROM TBDCMACM013C D4 WHERE D4.CMM_CD_DVSN_CD = '1000173' AND D4.CD = D2.JBGD_SECD ) AS RANK_CD_NM , D3.CNSTN_MBCMT_DEPT_NM , D1.EX_DVD_RTS_TG_YN , D1.SAL_SEI_YN , D1.SCS_YN , D1.RMRK_CN , D1.RGR_YN FROM TBCSPNBK001M D1 , TBDCMACM001M D2 , TBDCMACM002M D3 WHERE D1.TRGT_NOP_ENO = D2.TRGT_NOP_ENO AND D2.SRECS_DEPT_CD = D3.SRECS_DEPT_CD AND D1.MOUI_DVSN_CD = '0' D1.MOUI_NO = #mouiNo# D1.TRGT_NOP_ENO = #TRGT_NOP_ENO# D2.USER_NM LIKE '%' || #mouiNm# || '%' D1.SCS_YN = #scsYn# D2.RTRM_YMD IS NOT NULL ( D2.RTRM_YMD IS NULL OR D2.RTRM_YMD = '' ) ORDER BY MOUI_NO