SELECT /* CSPNBK221EGrtMouiListSelect */ D1.GRT_MOUI_NO,
    D3.USER_NM /* 사용자명 */,
    SUBSTR(D3.CRU_RRNO_SCRTY, 1, 6) || '-*******' AS SUBSTR,
    D4.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    D1.GRT_AM,
    D1.LEND_BL,
    D1.MOUI_NO,
    D6.USER_NM /* 사용자명 */,
    SUBSTR(D6.CRU_RRNO_SCRTY, 1, 6) || '-*******' AS CRU_RRNO_SCRTY /* 부패주민등록번호보안 */,
    D7.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBCSPNBK203D D1 , TBCSPNBK001M D2 , TBDCMACM001M D3 , TBDCMACM002M D4 , TBCSPNBK001M D5 , TBDCMACM001M D6 , TBDCMACM002M D7 WHERE D1.GRT_MOUI_NO = D2.MOUI_NO AND D2.TRGT_NOP_ENO = D3.TRGT_NOP_ENO AND D3.SRECS_DEPT_CD = D4.SRECS_DEPT_CD AND D1.MOUI_NO = D5.MOUI_NO AND D5.TRGT_NOP_ENO = D6.TRGT_NOP_ENO AND D6.SRECS_DEPT_CD = D7.SRECS_DEPT_CD AND D1.LEND_KND_CD = #grtLendKndCd# AND D1.CNL_YN = 'N' AND D1.GRT_MOUI_NO IS NOT NULL D1.GRT_MOUI_NO = #grtMouiNo# D3.USER_NM LIKE '%' || #grtMouiNm# || '%' D1.GRT_AM BETWEEN #grtBeginAm# AND #grtEndAm# D1.GRT_AM >= #grtBeginAm# D1.GRT_AM D1.MOUI_NO = #piGrtMouiNo# D6.USER_NM LIKE '%' || #piGrtMouiNm# || '%' D1.LEND_BL BETWEEN #lendBeginAm# AND #lendEndAm# D1.LEND_BL >= #lendBeginAm# D1.LEND_BL ORDER BY D1.GRT_MOUI_NO