SELECT CASE A.MOUI_DVSN_CD WHEN '0' THEN C.USER_NM ELSE A.OGNZ_NM END AS MOUI_DVSN_CD,
    A.MOUI_NO AS MOUI_NO,
    B.LEND_BL AS LEND_BL,
    CASE B.LEND_KND_CD WHEN '1' THEN '대출금' ELSE '임차보증대출금' END AS LEND_KND_CD,
    (SELECT AS SET_TXT
FROM TBCSPNBK003M WHERE OP_MTRL_MNG_CD = '4') AS NM, #strDT# AS LEND_DEAL_DT FROM TBCSPNBK001M A, TBCSPNBK205L B, TBDCMACM001M C WHERE A.MOUI_NO = B.MOUI_NO AND A.TRGT_NOP_ENO = C.TRGT_NOP_ENO AND B.CNL_YN = 'N' AND A.MOUI_NO = #MOUI_NO# AND B.LEND_KND_CD = #LEND_KND_CD# AND B.LEND_DEAL_DT = ( SELECT MAX(LEND_DEAL_DT)
FROM TBCSPNBK205L WHERE MOUI_NO = #MOUI_NO# AND LEND_KND_CD = #LEND_KND_CD# AND LEND_DEAL_DT <= #strDT# ) AND B.LEND_DEAL_DT_SLN = ( SELECT MAX(LEND_DEAL_DT_SLN)
FROM TBCSPNBK205L WHERE LEND_KND_CD = #LEND_KND_CD# AND MOUI_NO = #MOUI_NO# AND LEND_DEAL_DT = ( SELECT MAX(LEND_DEAL_DT)
FROM TBCSPNBK205L WHERE MOUI_NO = #MOUI_NO# AND LEND_KND_CD = #LEND_KND_CD# AND LEND_DEAL_DT <= #strDT# ) )