SELECT /* CSPNBK231QOrgAccListSelect */ D1.MOUI_DVSN_CD,
    ( SELECT D3.CD_NM
FROM TBDCMACM013C D3 WHERE D3.CMM_CD_DVSN_CD = '1000153' AND D3.CD = D1.MOUI_DVSN_CD ) AS MOUI_DVSN_CD_NM , D1.MOUI_NO , D1.OGNZ_NM || '(' || D1.MOUI_NO || ')' AS OGNZ_NM , D2.INV_OAA_DT , D2.INV_BL , '잔액증명서 출력' AS RPT_BTN , '거래내역조회' AS DEAL_LIST_BTN FROM TBCSPNBK001M D1 , TBCSPNBK101M D2 WHERE D1.MOUI_NO = D2.MOUI_NO AND D1.TRGT_NOP_ENO = ( SELECT TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBCSPNBK001M WHERE MOUI_DVSN_CD = '0' AND MOUI_NO = #mouiNo# ) AND D1.MOUI_DVSN_CD = '1' AND D1.SCS_YN = 'N' ORDER BY OGNZ_NM