UPDATE /* CSPNBK321ETracInvBlUpdate */ TBCSPNBK101M D2 SET INV_BL = INV_BL + ( SELECT D3.DDCT_AM
FROM TBCSPNBK104L D3 WHERE D3.PRCS_YM = #PRCS_YM# AND D3.TRAC_KND_CD = #tracKndCd# AND D3.DDCT_AM > 0 AND D3.MOUI_NO = D2.MOUI_NO) ,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE D2.MOUI_NO IN ( SELECT D1.MOUI_NO
FROM TBCSPNBK104L D1 WHERE D1.PRCS_YM = #PRCS_YM# AND D1.TRAC_KND_CD = #tracKndCd# AND D1.DDCT_AM > 0 )