WITH /* CSPNBK411ELendDealListSelect */ MAX_LEND_IJ AS ( SELECT MAX(D4.LEND_DEAL_DT_SLN) AS LEND_DEAL_DT_SLN
FROM TBCSPNBK205L D4 WHERE D4.LEND_KND_CD = #lendKndCd# AND D4.MOUI_NO = #mouiNo# AND D4.LEND_DEAL_DT = #lendDealDt# AND D4.CNL_YN = 'N' ), CLSNG_YN_DATA AS ( SELECT DISTINCT D4.DDLN_YN AS DDLN_YN /* 마감여부 */
FROM TBCSPNBK502M D4 WHERE D4.DEAL_DT = TO_CHAR(SYSDATE, 'YYYYMMDD') ) SELECT D1.LEND_KND_CD,
    D1.MOUI_NO,
    D1.LEND_DEAL_DT,
    D1.LEND_DEAL_DT_SLN,
    TO_CHAR(D1.PET_DT, 'HH24:MI') AS PET_DT /* 조작일시 */,
    D1.DEPTS_PMT_DVSN_CD,
    DECODE( D1.DEPTS_PMT_DVSN_CD, 'D', DEAL_PRN, NULL ) AS DEPTS_PMT_DVSN_CD,
    DECODE( D1.DEPTS_PMT_DVSN_CD, 'D' , ( CASE WHEN FLOOR( D1.DEAL_PRN / ((SELECT D1.SET_TXT
FROM TBCSPNBK003M D1 WHERE D1.OP_MTRL_MNG_CD = '7')*12) ) > 999 THEN CASE WHEN FLOOR( D1.DEAL_PRN / ((SELECT D1.SET_TXT
FROM TBCSPNBK003M D1 WHERE D1.OP_MTRL_MNG_CD = '7')*12) ) - TRUNC( FLOOR( D1.DEAL_PRN / ((SELECT D1.SET_TXT
FROM TBCSPNBK003M D1 WHERE D1.OP_MTRL_MNG_CD = '7')*12) ), -3 ) > 0 THEN TRUNC( FLOOR( D1.DEAL_PRN / ((SELECT D1.SET_TXT
FROM TBCSPNBK003M D1 WHERE D1.OP_MTRL_MNG_CD = '7')*12) ), -3 ) + 1000 ELSE TRUNC( FLOOR( D1.DEAL_PRN / ((SELECT D1.SET_TXT
FROM TBCSPNBK003M D1 WHERE D1.OP_MTRL_MNG_CD = '7')*12) ), -3 ) END ELSE 0 END ) , NULL ) AS EMN_RPM_PRN , DECODE( D1.DEPTS_PMT_DVSN_CD, 'C', DEAL_PRN, NULL ) AS C_DEAL_PRN , D1.INT_AM , D1.LEND_BL AS AF_LEND_BL , ( CASE WHEN D1.LEND_DEAL_DT = TO_CHAR(SYSDATE, 'YYYYMMDD') THEN '출력' ELSE NULL END ) AS BBK_PRT_BTN_NM , D1.LAG_WDL_RMK , ( CASE WHEN ( SELECT D5.DDLN_YN /* 마감여부 */
FROM CLSNG_YN_DATA D5 ) = 'N' AND D1.LEND_DEAL_DT = TO_CHAR(SYSDATE, 'YYYYMMDD') THEN CASE WHEN D1.LEND_DEAL_DT_SLN = ( SELECT D3.MAX_LEND_DEAL_DT_SLN
FROM MAX_LEND_IJ D3 ) THEN '취소' ELSE NULL END ELSE NULL END ) AS CNL_YN_BTN_NM , D1.CNL_YN , D1.CNL_RMK , D1.SMB_DEAL_DT_1 , D1.SMB_SLN_1 , D1.SMB_DEAL_DT_2 , D1.SMB_SLN_2 FROM TBCSPNBK205L D1 WHERE D1.LEND_KND_CD = #lendKndCd# AND D1.MOUI_NO = #mouiNo# AND D1.LEND_DEAL_DT = #lendDealDt# AND D1.CNL_YN = 'N' AND D1.LEND_DEAL_DVSN_CD = '0' ORDER BY D1.PET_DT