UPDATE /* CSPNBK411ELendPrvtBlBackUpdate */ TBCSPNBK203D D1 SET ( D1.LEND_BL, D1.GRT_BL ) = ( SELECT D2.DEAL_BK_LEND_BL,
    D2.DEAL_BK_GRT_BL
FROM TBCSPNBK204L D2 WHERE D2.LEND_KND_CD = D1.LEND_KND_CD AND D2.MOUI_NO = D1.MOUI_NO AND D2.LEND_SLN = D1.LEND_SLN AND D2.LEND_DEAL_DT = #LEND_DEAL_DT# AND D2.LEND_DEAL_DT_SLN = #LEND_DEAL_DT_SLN# ) ,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE ( D1.LEND_KND_CD, D1.MOUI_NO, D1.LEND_SLN ) IN ( SELECT D3.LEND_KND_CD,
    D3.MOUI_NO,
    D3.LEND_SLN
FROM TBCSPNBK204L D3 WHERE D3.LEND_KND_CD = #LEND_KND_CD# AND D3.MOUI_NO = #MOUI_NO# AND D3.LEND_DEAL_DT = #LEND_DEAL_DT# AND D3.LEND_DEAL_DT_SLN = #LEND_DEAL_DT_SLN# )