SELECT /* CSPNBK416LendRtsListSelect */ ( CASE WHEN D1.ITR_APC_DT D1.ITR_APC_DT ) AS END_YMD /* 종료일자 */
FROM TBCSPNBK201M D1 WHERE D1.LEND_KND_CD = #lendKndCd# AND D1.ITR_APC_DT BETWEEN ( SELECT MAX(D2.ITR_APC_DT)
FROM TBCSPNBK201M D2 WHERE D2.LEND_KND_CD = #lendKndCd# AND ITR_APC_DT <= #startDt#) AND ( SELECT MAX(D2.ITR_APC_DT)
FROM TBCSPNBK201M D2 WHERE D2.LEND_KND_CD = #lendKndCd# AND ITR_APC_DT < #endDt#) ORDER BY ITR_APC_DT