SELECT /* CSPNBK611QSlipListSelect */ D1.SMB_DEAL_DT,
    D1.SMB_SLN,
    TO_CHAR(D1.PET_DT, 'YYYY-MM-DD HH24:MI') AS PET_DT /* 조작일시 */,
    D1.LEDG_KND_CD,
    F_CODE_NM( '1000157', D1.LEDG_KND_CD ) AS F_CODE_NM,
    D1.CRDT_MNST_CLU_CD || '-' || D1.CRDT_TAX_SITM_CD AS CRDT_MNST_CLU_CD,
    ( SELECT D2.ACCT_NM
FROM TBCSPNBK501M D2 WHERE D2.MNST_CLU_CD = D1.CRDT_MNST_CLU_CD AND D2.TAXR_SITM_SECD = D1.CRDT_TAX_SITM_CD ) AS CRDT_ACCT_NM , DECODE(D1.DEPTS_PMT_DVSN_CD, 'C', DSPS_RQT_AMT, 0) AS CRDT_AM , D1.DS_MNST_CLU_CD || '-' || D1.DS_TAX_SITM_CD AS DS_ACCT_CD , ( SELECT D2.ACCT_NM
FROM TBCSPNBK501M D2 WHERE D2.MNST_CLU_CD = D1.DS_MNST_CLU_CD AND D2.TAXR_SITM_SECD = D1.DS_TAX_SITM_CD ) AS DS_ACCT_NM , DECODE(D1.DEPTS_PMT_DVSN_CD, 'D', DSPS_RQT_AMT, 0) AS DS_AM , '재출력' AS SLIP_PRT_NM FROM TBCSPNBK301M D1 WHERE D1.SMB_DEAL_DT = #smbDealDt# AND D1.CNL_YN = 'N' D1.LEDG_KND_CD = #ledgKndCd# (( D1.CRDT_MNST_CLU_CD = #mnstCluCd# AND D1.CRDT_TAX_SITM_CD = #taxSitmCd# ) OR ( D1.DS_MNST_CLU_CD = #mnstCluCd# AND D1.DS_TAX_SITM_CD = #taxSitmCd# ) ) ORDER BY D1.PET_DT DESC