SELECT /* CSPNBK711QSelectReport13 */ SUM(D1.DSPS_RQT_AMT) AS DSPS_RQT_AMT /* 처분요구금액 */
FROM TBCSPNBK502M D1 WHERE D1.DEAL_DT = ( SELECT MAX(DEAL_DT)
FROM TBCSPNBK502M WHERE DEAL_DT < #closingDt# AND SUBSTRING(DEAL_DT, 5, 2) != '13' /* 연말정산용 데이터 제외 */ AND MNST_CLU_CD = '110102' ) AND D1.MNST_CLU_CD = '110102'