SELECT A.VI_DT,
    A.VI_HM,
    SUBSTR(A.VI_HM, 0,2) AS SUBSTR,
    SUBSTR(A.VI_HM, 3,2) AS SUBSTR,
    TO_CHAR(TO_DATE(A.VI_DT), 'YYYY-MM-DD') || ' ' || TO_CHAR(TO_DATE(A.VI_HM, 'HH24:MI'), 'HH24:MI') AS TO_CHAR,
    A.VI_SLN,
    A.TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    A.OGDP_DEPT_CD /* 소속부서코드 */,
    A.INFO_RLPR_NM /* 정보관계자명 */,
    CASE WHEN A.JBGD_SECD IN (SELECT AS JBGD_SECD /* 직급구분코드 */
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000173') THEN A.JBGD_SECD ELSE '' END AS JBGD_SECD, CASE WHEN A.JBGD_SECD IN (SELECT CD
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000173') THEN F_CODE_NM('1000173', A.JBGD_SECD) ELSE '' END AS JBGD_NM, A.SLCT_OOFC_NM, A.VI_PEN_RGN_CD, F_CODE_NM('1000001', A.VI_PEN_RGN_CD) AS RGN_NM, A.VI_PEN_NAM, A.TPOF_NOPE, A.VI_PEN_NAM || '(' || A.TPOF_NOPE || ')' AS NM, A.CAR_NO, A.VI_PPS_CD, F_CODE_NM('1000284', A.VI_PPS_CD) AS VI_PPS_NM, A.VI_PPS_TXT, A.VI_WAY_CD, F_CODE_NM('1000285', A.VI_WAY_CD) AS VI_WAY_NM, #fromYmd# AS FROMYMD, #toYmd# AS TOYMD FROM TBCSPOEP001M A WHERE 1=1 AND A.VI_DT &gt;= #fromYmd# AND A.VI_DT &lt;= #toYmd# AND A.OGDP_DEPT_CD = #OGDP_DEPT_CD# AND A.JBGD_SECD = #JBGD_SECD# AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND A.SLCT_OOFC_NM LIKE '%'||#workPlace#||'%' AND A.CAR_NO LIKE '%'||#carNo#||'%' AND A.VI_PEN_NAM LIKE '%'||#name#||'%' ORDER BY A.VI_DT DESC, A.VI_SLN DESC