SELECT F_DPT_INFO(T1.SRECS_DEPT_CD , '1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    F_CODE_NM('1000176', T1.JBPS_CD) AS JBPS_NM /* 직위명 */,
    T1.RANK_NM_2,
    T1.USER_NM /* 사용자명 */,
    T1.R_HOM_TNB,
    T1.CNSTN_MBCMT_HOME_TELNO /* 자문위원자택전화번호 */,
    (SELECT AS DPT_SNO
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = T1.SRECS_DEPT_CD) DPT_SNO, NVL(T1.INSD_JBGD_SECD, '50') AS ORDER_INN_RANK_CD, T1.JBGD_SORT_SEQ ORDER_RANK_SRT_SEQ_NM, T1.NOW_JBGD_APNTMN_YMD ORDER_CRNT_RANK_APM_DT, T1.SRT_KY_NO ORDER_SRT_KY_NO, T1.CRNT_DPT_APM_DT ORDER_CRNT_DPT_APM_DT, SUBSTRING(T1.CRU_RRNO_SCRTY, 1, 6) ORDER_EIN, (CASE WHEN T1.SRECS_DEPT_CD='500000' OR T1.SRECS_DEPT_CD='120000' THEN '1' ELSE '2' END) AS SORT1, (CASE WHEN T1.SRECS_DEPT_CD='520000' THEN '1' ELSE '2' END) AS SORT2, (CASE WHEN T1.ACTL_OGDP_DEPT_CD = '510510' THEN '1' ELSE '2' END) AS SORT3, (CASE WHEN T1.ACTL_OGDP_DEPT_CD = '510520' THEN '1' ELSE '2' END) AS SORT4, (CASE WHEN T1.ACTL_OGDP_DEPT_CD = '510530' THEN '1' ELSE '2' END) AS SORT5, (CASE WHEN T1.ACTL_OGDP_DEPT_CD = '510540' THEN '1' ELSE '2' END) AS SORT6, (CASE WHEN T1.ACTL_OGDP_DEPT_CD = '510550' THEN '1' ELSE '2' END) AS SORT7 FROM TBDCMACM001M T1 WHERE USR_DVSN_CD in ('1','3') AND (TRGT_NOP_ENO '8') AND USER_ID not like 'itedu%' AND RQS_STA_CD ='3' AND SRECS_DEPT_CD <= '770770' /*파견자 포함*/ AND SRECS_DEPT_CD IN ('510000','160200','500000','120000','520000','530000') ORDER BY SORT1, SORT2, SORT3, SORT4, SORT5, SORT6, SORT7, DPT_SNO, ORDER_INN_RANK_CD, ORDER_RANK_SRT_SEQ_NM, ORDER_CRNT_RANK_APM_DT, ORDER_SRT_KY_NO, ORDER_CRNT_DPT_APM_DT, ORDER_EIN