SELECT A.BOKG_DT,
    B.LOUNGE_NM,
    (SELECT COUNT(BOKG_DT)
FROM TBCSPOEP012M T1, TBCSPOEP011M T2 WHERE 1=1 AND T1.LOUNGE_ID = T2.LOUNGE_ID AND T1.BOKG_DT= A.BOKG_DT) AS RESERV_CNT, /*예약자수*/ D.CNSTN_MBCMT_DEPT_NM, F_CODE_NM('1000173', C.JBGD_SECD) AS RANK_NAM, C.USER_NM, A.RSCH_ASMT_TTL FROM TBCSPOEP012M A, TBCSPOEP011M B, TBDCMACM001M C, TBDCMACM002M D WHERE 1=1 AND A.LOUNGE_ID = B.LOUNGE_ID AND A.BOKG_PEN_ID = C.USER_ID AND C.SRECS_DEPT_CD = D.SRECS_DEPT_CD AND A.COFM_CD IS NOT NULL AND A.BOKG_DT BETWEEN #toYmd# AND #fromYmd# AND A.COFM_CD = #rdbState# /* 상태값*/ GROUP BY A.BOKG_DT, D.CNSTN_MBCMT_DEPT_NM, C.USER_NM, C.JBGD_SECD,A.RSCH_ASMT_TTL,B.LOUNGE_NM ORDER BY A.BOKG_DT, D.CNSTN_MBCMT_DEPT_NM