SELECT B.CFR_RM_INSPC_RNM,
    D.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    COUNT(*) AS CNT
FROM TBCSPPMR002M A, TBCSPPMR001M B, TBDCMACM001M C, TBDCMACM002M D WHERE 1=1 AND A.CFR_RM_INSPC_RM_ID = B.CFR_RM_INSPC_RM_ID AND A.BOKG_PEN_ID = C.USER_ID AND C.SRECS_DEPT_CD = D.SRECS_DEPT_CD AND A.COFM_CD = '1' AND B.CFR_RM_INSPC_RM_DVSN_CD IN ('1','2') AND A.BOKG_DT BETWEEN #fromYmd# AND #toYmd# GROUP BY B.CFR_RM_INSPC_RNM, D.CNSTN_MBCMT_DEPT_NM ORDER BY B.CFR_RM_INSPC_RNM, D.CNSTN_MBCMT_DEPT_NM