SELECT A.CFR_RM_INSPC_RM_ID,
    A.CFR_RM_INSPC_RM_SRCH_SEQ,
    A.CFR_RM_INSPC_RNM,
    B.BOKG_APL_DH,
    B.BOKG_DT,
    TO_CHAR(TO_DATE( B.STR_HM,'HH24MI'),'HH24:MI') || ' ~ ' || TO_CHAR(TO_DATE(B.END_HM,'HH24MI'),'HH24:MI') AS TO_CHAR,
    B.RSCH_ASMT_TTL /* 연구과제제목 */,
    (SELECT AS CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM001M WHERE USER_ID = B.BOKG_PEN_ID) AS CRU_TELNO, CASE WHEN B.CFMTN_SECD IS NULL OR B.CFMTN_SECD ='0' OR B.CFMTN_SECD ='' THEN '미확정' ELSE '확정' END AS CFMTN_YN, CASE WHEN B.CFMTN_SECD IS NULL OR B.CFMTN_SECD ='0' OR B.CFMTN_SECD ='' THEN '' ELSE '1' END AS CONFIRM_YN, F_USR_INFO_BY_ID(B.BOKG_PEN_ID, '2') AS RESERVE_NM, C.TRGT_NOP_ENO, C.SRECS_DEPT_CD, F_DPT_INFO(C.SRECS_DEPT_CD, '1') AS CNSTN_MBCMT_DEPT_NM, B.USE_NM FROM TBCSPPMR001M A, TBCSPPMR002M B, TBDCMACM001M C, TBDCMACM002M D WHERE 1=1 AND (C.SRECS_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCds#))) ) AND A.CFR_RM_INSPC_RM_ID = B.CFR_RM_INSPC_RM_ID AND B.BOKG_PEN_ID = C.USER_ID AND A.CFR_RM_INSPC_RM_DVSN_CD NOT IN ('1','2') AND C.SRECS_DEPT_CD = D.SRECS_DEPT_CD AND #strSttBokgDt# AND #strEndBokgDt# >= B.BOKG_DT AND C.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND (C.SRECS_DEPT_CD = #SRECS_DEPT_CD# OR D.HIRK_DPT_CD = #SRECS_DEPT_CD#) AND C.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND B.CFMTN_SECD = #CFMTN_SECD# AND A.CFR_RM_INSPC_RM_DVSN_CD = #strSeatCd# AND B.USE_NM LIKE '%' || #strSeatUser# || '%' AND B.BOKG_PEN_ID = #strUsrId# ORDER BY B.BOKG_DT DESC